import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function Variouslessons() {
  const { id } = useParams();
  const [lesson, setLesson] = useState(null);
  const [signs, setSigns] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    setError(null);

    // ดึงข้อมูลบทเรียน
    const fetchLesson = fetch(`http://localhost:3000/lessons/${id}`).then((res) => {
      if (!res.ok) throw new Error("ไม่พบบทเรียน");
      return res.json();
    });

    // ดึงคำศัพท์ในบทเรียนนั้น
    const fetchSigns = fetch(`http://localhost:3000/signs/lesson/${id}`).then((res) => {
      if (!res.ok) throw new Error("ไม่พบคำศัพท์");
      return res.json();
    });

    Promise.all([fetchLesson, fetchSigns])
      .then(([lessonData, signsData]) => {
        setLesson(lessonData);

        // ตอนนี้ backend ส่ง Base64 มาแล้ว เราไม่ต้องแปลงอะไร
        setSigns(signsData);
      })
      .catch((err) => setError(err.message))
      .finally(() => setLoading(false));
  }, [id]);

  if (loading) return <p className="p-6">กำลังโหลดข้อมูล...</p>;
  if (error) return <p className="p-6 text-red-500">เกิดข้อผิดพลาด: {error}</p>;
  if (!lesson) return <p className="p-6">ไม่พบบทเรียน</p>;

  return (
    <div className="p-12 max-w-1xl mx-auto">
      {/* ชื่อบทเรียน */}
      <h2 className="text-2xl font-bold mt-4">
        บทเรียนที่ {lesson.lesson_number}: {lesson.title}
      </h2>
      <p className="mt-2 text-gray-700">{lesson.description}</p>

      {/* ส่วนคำศัพท์ / ท่ามือ */}
      <h3 className="text-xl font-semibold mt-6">คำศัพท์ / ท่ามือ</h3>
      {signs.length > 0 ? (
        <div className="grid grid-cols-1 gap-6 mt-4 w-full max-w-3xl mx-auto">
          {signs.map((sign) => (
            <div
              key={sign.sign_id}
              className="flex items-center bg-white rounded-lg shadow p-4"
            >
              {/* รูป / วิดีโอ */}
              <div className="w-1/2 flex justify-center">
                {sign.image_data ? (
                  <img
                    src={`data:image/jpeg;base64,${sign.image_data}`}
                    alt={sign.word}
                    className="max-w-full max-h-[400px] object-contain rounded"
                  />
                ) : sign.video_url ? (
                  <video
                    controls
                    className="max-w-full max-h-[400px] rounded-lg"
                    src={sign.video_url}
                  />
                ) : (
                  <div className="text-gray-400 text-sm">ไม่มีสื่อประกอบ</div>
                )}
              </div>

              {/* ข้อความ */}
              <div className="w-1/2 text-center">
                <h4 className="font-bold text-xl">{sign.word}</h4>
                <p className="text-lg text-gray-700">{sign.meaning}</p>
                <p className="text-sm text-gray-500">{sign.description}</p>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="mt-2 text-gray-500">ยังไม่มีคำศัพท์ในบทนี้</p>
      )}
    </div>
  );
}

export default Variouslessons;
