import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function Testexercise() {
    const { id } = useParams();
    const [lesson, setLesson] = useState(null);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [selectedOption, setSelectedOption] = useState("");
    const [score, setScore] = useState(0);
    const [showResult, setShowResult] = useState(false);
    const [userAnswers, setUserAnswers] = useState([]); // เก็บคำตอบที่เลือก
    const [showAnswers, setShowAnswers] = useState(false); // แสดงผลถูกผิด

    useEffect(() => {
        fetch(`http://localhost:3000/exercises/lesson/${id}`)
            .then(res => res.json())
            .then(data => setLesson(data))
            .catch(err => console.error("Error:", err));
    }, [id]);

    if (!lesson) return <p>Loading...</p>;

    const questions = lesson.questions;

    const handleNext = () => {
        // เก็บคำตอบที่เลือกไว้
        setUserAnswers([...userAnswers, selectedOption]);

        if (selectedOption === questions[currentQuestion].answer) {
            setScore(score + 1);
        }

        setSelectedOption("");

        if (currentQuestion + 1 < questions.length) {
            setCurrentQuestion(currentQuestion + 1);
        } else {
            setShowResult(true);
        }
    };

    const handleRestart = () => {
        setCurrentQuestion(0);
        setSelectedOption("");
        setScore(0);
        setShowResult(false);
        setUserAnswers([]);
        setShowAnswers(false);
    };

    if (showResult) {
        return (
            <div className="px-6 py-12">
                <h1 className="text-3xl font-bold">ผลลัพธ์ของคุณ</h1>
                <p className="mt-4 text-lg">คุณทำถูก {score} / {questions.length} ข้อ</p>

                <div className="mt-6 flex gap-4">
                    <button
                        className="px-4 py-2 bg-green-500 text-white rounded"
                        onClick={handleRestart}
                    >
                        ทำอีกครั้ง
                    </button>
                    <button
                        className="px-4 py-2 bg-blue-500 text-white rounded"
                        onClick={() => setShowAnswers(true)}
                    >
                        เช็คข้อที่ถูก/ผิด
                    </button>
                </div>

                {showAnswers && (
                    <div className="mt-8">
                        <h2 className="text-2xl font-semibold mb-4">ผลการทำข้อสอบ</h2>
                        {questions.map((q, idx) => {
                            const isCorrect = userAnswers[idx] === q.answer;
                            return (
                                <div
                                    key={idx}
                                    className={`p-4 mb-3 rounded border ${isCorrect ? "bg-green-100 border-green-500" : "bg-red-100 border-red-500"}`}
                                >
                                    <p className="font-semibold">{idx + 1}. {q.question}</p>
                                    <p className="mt-2">คำตอบของคุณ: <span className={isCorrect ? "text-green-700" : "text-red-700"}>{userAnswers[idx]}</span></p>
                                    {!isCorrect && (
                                        <p className="mt-1 text-green-700">คำตอบที่ถูกต้อง: {q.answer}</p>
                                    )}
                                </div>
                            );
                        })}
                    </div>
                )}
            </div>
        );
    }

    const question = questions[currentQuestion];

    return (
        <div className="px-6 py-12">
            <h1 className="text-3xl font-bold">{lesson.title}</h1>
            <p className="mt-2 text-gray-600">คำถาม {currentQuestion + 1} / {questions.length}</p>

            <div className="mt-6 bg-gray-100 p-4 rounded-lg shadow">
                <h3 className="text-lg font-semibold">{question.question}</h3>
                <div className="mt-4 flex flex-col gap-3">
                    {question.options.map((opt, idx) => (
                        <button
                            key={idx}
                            className={`p-3 rounded border text-left 
                                ${selectedOption === opt ? "bg-blue-500 text-white" : "bg-white"}`}
                            onClick={() => setSelectedOption(opt)}
                        >
                            {opt}
                        </button>
                    ))}
                </div>
                <button
                    className="mt-4 px-4 py-2 bg-green-500 text-white rounded"
                    onClick={handleNext}
                    disabled={!selectedOption}
                >
                    {currentQuestion + 1 < questions.length ? "ถัดไป" : "สรุปผล"}
                </button>
            </div>
        </div>
    );
}

export default Testexercise;
