import { Edit, Trash2 } from "lucide-react";
import { useNavigate } from "react-router-dom";

export default function ContentSection({ title, items, fetchData, type }) {
  const navigate = useNavigate();

  // ฟังก์ชันลบข้อมูล
  const handleDelete = async (id) => {
    if (!window.confirm("คุณแน่ใจว่าต้องการลบข้อมูลนี้หรือไม่?")) return;

    try {
      await fetch(`http://localhost:3000/${type}/${id}`, {
        method: "DELETE",
      });
      alert("ลบข้อมูลเรียบร้อยแล้ว");
      fetchData(); // รีเฟรชข้อมูลใหม่
    } catch (err) {
      console.error("Error deleting:", err);
      alert("เกิดข้อผิดพลาดในการลบข้อมูล");
    }
  };

  // ฟังก์ชันแก้ไขข้อมูล
  const handleEdit = (id) => {
    navigate(`/edit${type}/${id}`);
  };

  return (
    <div className="bg-white rounded-xl shadow-md p-5 w-full">
      <div className="flex justify-between items-center mb-3">
        <h3 className="font-semibold text-lg">{title}</h3>
      </div>

      <div className="max-h-48 overflow-y-auto pr-2 scrollbar-thin scrollbar-thumb-gray-300 scrollbar-track-gray-100 rounded-md">
        {items.length === 0 ? (
          <p className="text-gray-400 text-sm">ไม่มีข้อมูล</p>
        ) : (
          <ul className="text-sm space-y-2">
            {items.map((item) => {
              const name = item.title || item.word || item.question || "N/A";
              const id = item.lesson_id || item.word_id || item.question_id;

              return (
                <li
                  key={id}
                  className="flex justify-between items-center border-b py-1"
                >
                  <span>{name}</span>
                  <div className="flex gap-2">
                    <Edit
                      className="w-4 h-4 cursor-pointer text-gray-500 hover:text-blue-500"
                      onClick={() => handleEdit(id)}
                    />
                    <Trash2
                      className="w-4 h-4 cursor-pointer text-gray-500 hover:text-red-500"
                      onClick={() => handleDelete(id)}
                    />
                  </div>
                </li>
              );
            })}
          </ul>
        )}
      </div>
    </div>
  );
}
