import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

export default function EditSign() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [sign, setSign] = useState({
    word: "",
    meaning: "",
    image_data: null,
    video_url: "",
    description: "",
  });

  useEffect(() => {
    fetch(`http://localhost:3000/signs/${id}`)
      .then(res => res.json())
      .then(data => setSign(data))
      .catch(err => console.error(err));
  }, [id]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "image") {
      const reader = new FileReader();
      reader.onloadend = () => setSign(prev => ({ ...prev, image_data: reader.result }));
      reader.readAsDataURL(files[0]);
    } else {
      setSign(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!window.confirm("คุณแน่ใจว่าต้องการบันทึกการแก้ไข?")) return;

    try {
      const res = await fetch(`http://localhost:3000/signs/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(sign),
      });
      if (!res.ok) throw new Error("Update failed");
      alert("แก้ไขเรียบร้อยแล้ว");
      navigate("/dashboard");
    } catch (err) {
      console.error(err);
      alert("เกิดข้อผิดพลาดในการแก้ไข");
    }
  };

  return (
    <div className="max-w-lg mx-auto p-8 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-6">แก้ไขคำศัพท์</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="word"
          value={sign.word}
          onChange={handleChange}
          placeholder="คำศัพท์"
          className="w-full border px-3 py-2 rounded-md"
        />
        <input
          type="text"
          name="meaning"
          value={sign.meaning}
          onChange={handleChange}
          placeholder="ความหมาย"
          className="w-full border px-3 py-2 rounded-md"
        />
        <textarea
          name="description"
          value={sign.description}
          onChange={handleChange}
          placeholder="รายละเอียดเพิ่มเติม"
          className="w-full border px-3 py-2 rounded-md"
        />
        <input type="file" name="image" onChange={handleChange} />
        {sign.image_data && <img src={sign.image_data} alt="Sign" className="mt-2 w-32 h-32 object-cover" />}
        <input
          type="text"
          name="video_url"
          value={sign.video_url}
          onChange={handleChange}
          placeholder="Video URL"
          className="w-full border px-3 py-2 rounded-md"
        />
        <button type="submit" className="bg-blue-600 text-white px-5 py-2 rounded-md">บันทึก</button>
      </form>
    </div>
  );
}
