import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import useSound from "use-sound";
import alertSound from "../assets/alert.mp3"; // üîî ‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÑ‡∏ü‡∏•‡πå‡πÄ‡∏™‡∏µ‡∏¢‡∏á

const Testexercise = () => {
  const { lessonNumber } = useParams();
  const navigate = useNavigate();

  const [lesson, setLesson] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [userAnswer, setUserAnswer] = useState("");
  const [score, setScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(15);
  const [gameOver, setGameOver] = useState(false);
  const [playerName, setPlayerName] = useState("");
  const [showStart, setShowStart] = useState(true);
  const [playAlert] = useSound(alertSound);

  // ‡πÇ‡∏´‡∏•‡∏î‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡∏™‡∏π‡∏á‡∏™‡∏∏‡∏î‡∏à‡∏≤‡∏Å localStorage
  const [highScore, setHighScore] = useState(
    JSON.parse(localStorage.getItem("highScore")) || { name: "-", score: 0 }
  );

  // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≤‡∏Å API
  useEffect(() => {
    fetch(`http://localhost:3000/exercises/lesson/${lessonNumber}`)
      .then((res) => res.json())
      .then((data) => {
        setLesson(data);
        // ‚úÖ ‡∏™‡∏∏‡πà‡∏°‡∏Ñ‡∏≥‡∏ñ‡∏≤‡∏°
        setQuestions(data.questions.sort(() => Math.random() - 0.5));
      })
      .catch((err) => console.error("Error:", err));
  }, [lessonNumber]);

  // ‚è∞ ‡∏ô‡∏±‡∏ö‡πÄ‡∏ß‡∏•‡∏≤‡∏ñ‡∏≠‡∏¢‡∏´‡∏•‡∏±‡∏á
  useEffect(() => {
    if (showStart || gameOver || questions.length === 0) return;

    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev === 1) {
          playAlert();
          handleNextQuestion(false);
          return 15;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [currentQuestion, showStart, gameOver, questions]);

  const handleAnswerSubmit = (e) => {
    e.preventDefault();
    const correct = userAnswer.trim() === questions[currentQuestion].answer;
    handleNextQuestion(correct);
  };

  const handleNextQuestion = (correct) => {
    if (correct) setScore(score + 1);

    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
      setUserAnswer("");
      setTimeLeft(15);
    } else {
      setGameOver(true);
      if (score > highScore.score) {
        const newHigh = { name: playerName, score };
        setHighScore(newHigh);
        localStorage.setItem("highScore", JSON.stringify(newHigh));
      }
    }
  };

  const startGame = () => {
    if (!playerName) {
      alert("‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡πÉ‡∏™‡πà‡∏ä‡∏∑‡πà‡∏≠‡∏Å‡πà‡∏≠‡∏ô‡πÄ‡∏£‡∏¥‡πà‡∏°‡πÄ‡∏Å‡∏°");
      return;
    }
    setShowStart(false);
    setGameOver(false);
    setScore(0);
    setCurrentQuestion(0);
    setTimeLeft(15);
  };

  if (!lesson && showStart === false) return <p>Loading...</p>;

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-b from-blue-600 to-indigo-700 text-white p-6">
      {showStart ? (
        <motion.div
          className="bg-white/10 p-8 rounded-2xl shadow-lg text-center max-w-md w-full"
          initial={{ scale: 0.8, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
        >
          <h1 className="text-3xl font-bold mb-6">üñêÔ∏è ‡πÄ‡∏Å‡∏°‡∏ù‡∏∂‡∏Å‡∏†‡∏≤‡∏©‡∏≤‡∏°‡∏∑‡∏≠‡πÑ‡∏ó‡∏¢</h1>
          <input
            type="text"
            placeholder="‡πÉ‡∏™‡πà‡∏ä‡∏∑‡πà‡∏≠‡∏Ç‡∏≠‡∏á‡∏Ñ‡∏∏‡∏ì..."
            value={playerName}
            onChange={(e) => setPlayerName(e.target.value)}
            className="p-3 w-full rounded-md text-black mb-4"
          />
          <button
            onClick={startGame}
            className="bg-yellow-400 text-black font-semibold py-2 px-6 rounded-md hover:bg-yellow-500"
          >
            ‡πÄ‡∏£‡∏¥‡πà‡∏°‡πÄ‡∏Å‡∏°
          </button>

          <div className="mt-6 text-sm text-gray-200">
            üèÜ ‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡∏™‡∏π‡∏á‡∏™‡∏∏‡∏î: {highScore.name} ({highScore.score})
          </div>
        </motion.div>
      ) : gameOver ? (
        <motion.div
          className="bg-white/10 p-8 rounded-2xl shadow-lg text-center max-w-md w-full"
          initial={{ scale: 0.8, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
        >
          <h2 className="text-3xl font-bold mb-4">üéâ ‡∏à‡∏ö‡πÄ‡∏Å‡∏°!</h2>
          <p className="text-xl mb-2">‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡∏Ç‡∏≠‡∏á‡∏Ñ‡∏∏‡∏ì: {score}</p>
          <p className="text-lg text-yellow-300 mb-6">
            ‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡∏™‡∏π‡∏á‡∏™‡∏∏‡∏î: {highScore.name} ({highScore.score})
          </p>
          <button
            onClick={() => setShowStart(true)}
            className="bg-green-400 text-black font-semibold py-2 px-6 rounded-md hover:bg-green-500"
          >
            ‡πÄ‡∏•‡πà‡∏ô‡∏≠‡∏µ‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á
          </button>
        </motion.div>
      ) : (
        <AnimatePresence mode="wait">
          <motion.div
            key={currentQuestion}
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -30 }}
            transition={{ duration: 0.5 }}
            className="bg-white/10 p-8 rounded-2xl shadow-lg text-center max-w-md w-full"
          >
            <h2 className="text-2xl font-semibold mb-4">
              ‡∏Ñ‡∏≥‡∏ñ‡∏≤‡∏° {currentQuestion + 1}/{questions.length}
            </h2>

            {/* ‚úÖ Progress Bar */}
            <div className="w-full bg-gray-300 rounded-full h-3 mb-6">
              <div
                className="bg-green-400 h-3 rounded-full transition-all duration-500"
                style={{
                  width: `${((currentQuestion + 1) / questions.length) * 100}%`,
                }}
              ></div>
            </div>

            <p className="text-xl mb-6">{questions[currentQuestion].question}</p>

            {/* ‚è∞ ‡∏ï‡∏±‡∏ß‡∏à‡∏±‡∏ö‡πÄ‡∏ß‡∏•‡∏≤ */}
            <div className="text-yellow-300 font-bold mb-4 text-lg">
              ‡πÄ‡∏´‡∏•‡∏∑‡∏≠‡πÄ‡∏ß‡∏•‡∏≤: {timeLeft} ‡∏ß‡∏¥
            </div>

            {/* ‡πÅ‡∏ö‡∏ö‡∏ü‡∏≠‡∏£‡πå‡∏°‡∏ï‡∏≠‡∏ö */}
            <form onSubmit={handleAnswerSubmit}>
              <input
                type="text"
                value={userAnswer}
                onChange={(e) => setUserAnswer(e.target.value)}
                placeholder="‡∏û‡∏¥‡∏°‡∏û‡πå‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö..."
                className="p-3 w-full rounded-md text-black mb-4"
              />
              <button
                type="submit"
                className="bg-blue-400 text-black font-semibold py-2 px-6 rounded-md hover:bg-blue-500"
              >
                ‡∏ï‡∏≠‡∏ö
              </button>
            </form>
          </motion.div>
        </AnimatePresence>
      )}
    </div>
  );
};

export default Testexercise;
