import { useEffect, useState, useCallback } from "react";
import { useParams } from "react-router-dom";

function Testexercise() {
  const { lessonNumber } = useParams();

  const [lesson, setLesson] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [userAnswers, setUserAnswers] = useState([]);
  const [leaderboard, setLeaderboard] = useState([]);

  const userId = localStorage.getItem("userId") || 0;
  const token = localStorage.getItem("token") || "";

  // ‡πÇ‡∏´‡∏•‡∏î lesson
  useEffect(() => {
    fetch(`http://localhost:3000/exercises/lesson/${lessonNumber}`)
      .then((res) => res.json())
      .then((data) => setLesson(data))
      .catch((err) => console.error("Error fetching lesson:", err));
  }, [lessonNumber]);

  // ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏™‡πà‡∏á‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡πÅ‡∏•‡∏∞‡∏î‡∏∂‡∏á leaderboard
  const handleFinish = useCallback(async () => {
    if (!lesson) return;

    // ‡πÄ‡∏Å‡πá‡∏ö‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö‡∏™‡∏∏‡∏î‡∏ó‡πâ‡∏≤‡∏¢
    const answer = selectedOption || "‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å";
    setUserAnswers((prev) => [...prev, answer]);
    const finalScore =
      score + (answer === lesson.questions[currentQuestion].answer ? 1 : 0);
    setScore(finalScore);

    try {
      if (userId && token) {
        // POST ‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡πÑ‡∏õ backend
        await fetch("http://localhost:3000/scores", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
          },
          body: JSON.stringify({
            user_id: userId,
            lesson_number: lessonNumber,
            exercise_id: lessonNumber, // ‡∏´‡∏£‡∏∑‡∏≠‡πÉ‡∏ä‡πâ exercise.id ‡∏à‡∏£‡∏¥‡∏á
            score: finalScore,
          }),
        });

        // GET leaderboard
        const lbRes = await fetch(
          `http://localhost:3000/scores?lesson_number=${lessonNumber}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        const lbData = await lbRes.json();
        lbData.sort((a, b) => b.score - a.score);
        setLeaderboard(lbData.slice(0, 5));
      }
    } catch (err) {
      console.error("Save score error:", err);
    }

    setShowResult(true);
  }, [currentQuestion, selectedOption, score, lesson, userId, token, lessonNumber]);

  if (!lesson) return <p>Loading...</p>;

  const questions = lesson.questions;
  const question = questions[currentQuestion];

  // ‡∏Å‡∏î‡∏õ‡∏∏‡πà‡∏°‡∏ñ‡∏±‡∏î‡πÑ‡∏õ
  const handleNext = () => {
    const answer = selectedOption || "‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å";
    setUserAnswers((prev) => [...prev, answer]);
    if (answer === question.answer) setScore((prev) => prev + 1);

    setSelectedOption("");
    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      handleFinish();
    }
  };

  // ‡∏Å‡∏î restart
  const handleRestart = () => {
    setCurrentQuestion(0);
    setSelectedOption("");
    setScore(0);
    setShowResult(false);
    setUserAnswers([]);
    setLeaderboard([]);
  };

  // --- Minimal UI ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏ó‡∏î‡∏™‡∏≠‡∏ö logic ---
  if (showResult) {
    return (
      <div>
        <h2>‡∏Ñ‡∏∏‡∏ì‡∏ó‡∏≥‡πÅ‡∏ö‡∏ö‡∏ó‡∏î‡∏™‡∏≠‡∏ö‡πÄ‡∏™‡∏£‡πá‡∏à‡πÅ‡∏•‡πâ‡∏ß üéâ</h2>
        <p>‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô: {score} / {questions.length}</p>

        <h3>Leaderboard</h3>
        <ol>
          {leaderboard.map((item, idx) => (
            <li key={idx}>
              {item.username} - {item.score} ‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô
              {item.user_id === userId ? " (‡∏Ñ‡∏∏‡∏ì)" : ""}
            </li>
          ))}
        </ol>

        <button onClick={handleRestart}>‡∏ó‡∏≥‡∏≠‡∏µ‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á</button>
      </div>
    );
  }

  return (
    <div>
      <h2>‡∏Ç‡πâ‡∏≠ {currentQuestion + 1}: {question.question}</h2>

      {question.options.map((opt, idx) => (
        <button
          key={idx}
          onClick={() => setSelectedOption(opt)}
          style={{ background: selectedOption === opt ? "lightblue" : "white" }}
        >
          {opt}
        </button>
      ))}

      <div>
        <button onClick={handleNext} disabled={!selectedOption}>
          {currentQuestion + 1 < questions.length ? "‡∏ñ‡∏±‡∏î‡πÑ‡∏õ" : "‡∏™‡∏£‡∏∏‡∏õ‡∏ú‡∏•"}
        </button>
      </div>
    </div>
  );
}

export default Testexercise;
