import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

export default function EditLesson() {
  const { id } = useParams();
  const navigate = useNavigate();
  const MySwal = withReactContent(Swal);

  const [lesson, setLesson] = useState({
    lesson_id: "",
    lesson_number: "",
    lesson_title: "",
    description: "",
    image_file: null,    // เก็บไฟล์จริง
    image_preview: null  // สำหรับ preview
  });

  // โหลดข้อมูลบทเรียนตาม id
  useEffect(() => {
    fetch(`http://localhost:3000/lessons/${id}`)
      .then(res => res.json())
      .then(data => {
        setLesson(prev => ({
          ...prev,
          lesson_id: data.lesson_id,
          lesson_number: data.lesson_number,
          lesson_title: data.lesson_title,
          description: data.description,
          image_preview: data.image_url || null // สมมติ backend ส่ง URL ของรูป
        }));
      })
      .catch(err => console.error("Error fetching lesson:", err));
  }, [id]);

  // handleChange สำหรับ input ทุกชนิด
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "image" && files && files.length > 0) {
      const file = files[0];
      setLesson(prev => ({
        ...prev,
        image_file: file,
        image_preview: URL.createObjectURL(file) // preview รูปใหม่
      }));
    } else {
      setLesson(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const result = await MySwal.fire({
      title: "ยืนยันการบันทึก",
      showCancelButton: true,
      confirmButtonText: "ตกลง",
      cancelButtonText: "ยกเลิก"
    });
    if (!result.isConfirmed) return;

    try {
      const formData = new FormData();
      formData.append("lesson_number", lesson.lesson_number);
      formData.append("lesson_title", lesson.lesson_title);
      formData.append("description", lesson.description);
      if (lesson.image_file) {
        formData.append("image", lesson.image_file); // ส่งไฟล์จริง
      }

      const res = await fetch(`http://localhost:3000/lessons/${lesson.lesson_id}`, {
        method: "PUT",
        body: formData
      });

      if (!res.ok) {
        const text = await res.text();
        throw new Error(`Update failed: ${text}`);
      }

      MySwal.fire("สำเร็จ!", "แก้ไขบทเรียนเรียบร้อยแล้ว", "success");
      navigate("/dashboard");
    } catch (err) {
      console.error("Error updating lesson:", err);
      MySwal.fire(
        "ผิดพลาด",
        `เกิดข้อผิดพลาดในการแก้ไขบทเรียน\n${err.message}`,
        "error"
      );
    }
  };

  return (
    <div className="max-w-lg mx-auto p-8 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-6">แก้ไขบทเรียน</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="lesson_number"
          value={lesson.lesson_number}
          onChange={handleChange}
          placeholder="Lesson Number"
          className="w-full border px-3 py-2 rounded-md"
        />
        <input
          type="text"
          name="lesson_title"
          value={lesson.lesson_title}
          onChange={handleChange}
          placeholder="Title"
          className="w-full border px-3 py-2 rounded-md"
        />
        <textarea
          name="description"
          value={lesson.description}
          onChange={handleChange}
          placeholder="Description"
          className="w-full border px-3 py-2 rounded-md"
        />
        <input type="file" name="image" onChange={handleChange} />
        {lesson.image_preview && (
          <img
            src={lesson.image_preview}
            alt="Lesson Preview"
            className="mt-2 w-32 h-32 object-cover"
          />
        )}
        <button
          type="submit"
          className="bg-blue-600 text-white px-5 py-2 rounded-md"
        >
          บันทึก
        </button>
      </form>
    </div>
  );
}
