import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function Lessons() {
  const [lessons, setLessons] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/lessons")
      .then((res) => res.json())
      .then((data) => setLessons(data))
      .catch((err) => console.error("Error:", err));
  }, []);

  return (
    <div className="px-6 py-12">
      <h1 className="text-3xl font-bold">บทเรียนทั้งหมด</h1>
      <p className="text-gray-600 mt-2">บทเรียนทั้งหมด {lessons.length} บท</p>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-8">
        {lessons.map((lesson, index) => (
          <Link
            to={`/lessons/${lesson.id}`}
            key={lesson.id || index}
            className="flex flex-col md:flex-row items-start bg-gray-100 p-6 rounded-lg shadow-sm hover:shadow-md transition cursor-pointer"
          >
            {/* Lesson Image */}
            {lesson.image_url && (
              <img
                src={`/${lesson.image_url}`}
                alt={lesson.title}
                className="w-full md:w-32 h-32 object-cover rounded-lg"
              />
            )}

            {/* Lesson Content */}
            <div className="ml-0 md:ml-4 mt-4 md:mt-0">
              <div className="flex items-center">
                {/* Circle Number */}
                <div className="flex-shrink-0 w-12 h-12 flex items-center justify-center bg-blue-600 text-white rounded-full text-lg font-bold">
                  {lesson.id || index + 1}
                </div>

                <h3 className="font-semibold text-lg ml-4">
                  บทที่ {lesson.id || index + 1}: {lesson.title}
                </h3>
              </div>
              <p className="text-gray-600 text-sm mt-1">{lesson.description}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default Lessons;
