import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

export default function EditExercise() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [exercise, setExercise] = useState({
    question: "",
    option_a: "",
    option_b: "",
    option_c: "",
    option_d: "",
    correct_answer: "",
    image_data: null
  });

  useEffect(() => {
    fetch(`http://localhost:3000/exercises/${id}`)
      .then(res => res.json())
      .then(data => setExercise(data))
      .catch(err => console.error(err));
  }, [id]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "image") {
      const reader = new FileReader();
      reader.onloadend = () => setExercise(prev => ({ ...prev, image_data: reader.result }));
      reader.readAsDataURL(files[0]);
    } else {
      setExercise(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!window.confirm("คุณแน่ใจว่าต้องการบันทึกการแก้ไข?")) return;

    try {
      const res = await fetch(`http://localhost:3000/exercises/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(exercise),
      });
      if (!res.ok) throw new Error("Update failed");
      alert("แก้ไขเรียบร้อยแล้ว");
      navigate("/dashboard");
    } catch (err) {
      console.error(err);
      alert("เกิดข้อผิดพลาดในการแก้ไข");
    }
  };

  return (
    <div className="max-w-lg mx-auto p-8 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-6">แก้ไขคำถาม</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input type="text" name="question" value={exercise.question} onChange={handleChange} placeholder="คำถาม" className="w-full border px-3 py-2 rounded-md" />
        <input type="text" name="option_a" value={exercise.option_a} onChange={handleChange} placeholder="ตัวเลือก A" className="w-full border px-3 py-2 rounded-md" />
        <input type="text" name="option_b" value={exercise.option_b} onChange={handleChange} placeholder="ตัวเลือก B" className="w-full border px-3 py-2 rounded-md" />
        <input type="text" name="option_c" value={exercise.option_c} onChange={handleChange} placeholder="ตัวเลือก C" className="w-full border px-3 py-2 rounded-md" />
        <input type="text" name="option_d" value={exercise.option_d} onChange={handleChange} placeholder="ตัวเลือก D" className="w-full border px-3 py-2 rounded-md" />
        <input type="text" name="correct_answer" value={exercise.correct_answer} onChange={handleChange} placeholder="คำตอบที่ถูกต้อง" className="w-full border px-3 py-2 rounded-md" />
        <input type="file" name="image" onChange={handleChange} />
        {exercise.image_data && <img src={exercise.image_data} alt="Exercise" className="mt-2 w-32 h-32 object-cover" />}
        <button type="submit" className="bg-blue-600 text-white px-5 py-2 rounded-md">บันทึก</button>
      </form>
    </div>
  );
}
