import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import heroImage from "../assets/hero.webp";
import { jwtDecode } from "jwt-decode";

function Register() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();

  const saveUserData = (data) => {
    localStorage.setItem("user", JSON.stringify(data));
    localStorage.setItem("username", data.username);
    localStorage.setItem("userId", data.user_id);
    if (data.token) localStorage.setItem("token", data.token);
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    if (!username || !email || !password) {
      setError("กรุณากรอกข้อมูลให้ครบ");
      setLoading(false);
      return;
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setError("กรุณากรอก Email ให้ถูกต้อง");
      setLoading(false);
      return;
    }

    try {
      // 1️⃣ Register
      const res = await fetch("http://localhost:3000/user/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, email, password }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Register failed");

      // 2️⃣ Login อัตโนมัติ
      const resLogin = await fetch("http://localhost:3000/user/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });
      const dataLogin = await resLogin.json();
      if (!resLogin.ok) throw new Error(dataLogin.message || "Login failed");

      // 3️⃣ Decode token และ save ลง localStorage
      const decoded = jwtDecode(dataLogin.token);
      const userData = {
        user_id: decoded.user_id,
        username: decoded.username,
        token: dataLogin.token,
      };
      saveUserData(userData);

      // 4️⃣ Navigate หน้า Home
      navigate("/", { replace: true });
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      className="min-h-screen flex items-center justify-center bg-gray-100 bg-cover bg-center relative"
      style={{ backgroundImage: `url(${heroImage})` }}
    >
      <div className="absolute inset-0 bg-black/50"></div>
      <div className="relative z-10 w-full max-w-sm sm:max-w-md bg-white/95 backdrop-blur-md p-10 rounded-3xl shadow-2xl flex flex-col items-center">
        <h2 className="text-3xl sm:text-4xl font-extrabold text-gray-800 mb-8">
          Register
        </h2>

        {error && <p className="text-red-500 text-sm text-center mb-2">{error}</p>}

        <form className="w-full space-y-5">
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="w-full px-5 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
            required
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full px-5 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full px-5 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
            required
          />

          <button
            onClick={handleRegister}
            disabled={loading}
            className={`w-full py-3 rounded-xl text-white font-semibold flex items-center justify-center ${
              loading ? "bg-green-300 cursor-not-allowed" : "bg-green-600 hover:bg-green-700"
            } transition`}
          >
            {loading ? "Processing..." : "Register"}
          </button>
        </form>

        <button
          onClick={() => navigate("/login")}
          className="mt-6 w-full py-2 text-center rounded-xl border border-gray-400 text-gray-700 hover:bg-gray-100 transition"
        >
          กลับไปหน้า Login
        </button>
      </div>
    </div>
  );
}

export default Register;
