import { useEffect, useState, useRef } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { ChevronDown, MoreVertical } from "lucide-react";

function Navbar() {
  const navigate = useNavigate();
  const location = useLocation();
  const [lessons, setLessons] = useState([]);
  const [openMenu, setOpenMenu] = useState(null);
  const [openUserMenu, setOpenUserMenu] = useState(false);
  const timeoutRef = useRef(null);

  const getUsername = () => JSON.parse(localStorage.getItem("user") || "{}").username || null;
  const [username, setUsername] = useState(getUsername());

  useEffect(() => {
    fetch("http://localhost:3000/lessons")
      .then(res => res.json())
      .then(data => setLessons(data))
      .catch(err => console.error(err));
  }, []);

  // ฟังชั่นอัปเดต username แบบ realtime
  const refreshUsername = () => setUsername(getUsername());
  useEffect(() => {
    window.addEventListener("userChanged", refreshUsername);
    return () => window.removeEventListener("userChanged", refreshUsername);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUsername(null);
    navigate("/login");
  };

  // ... โค้ดเมนู dropdown เดิม

  return (
    <nav className="bg-white shadow-md p-4 sticky top-0 z-50">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-xl font-bold text-blue-600">THAI SIGN</h1>
        <ul className="flex gap-4 items-center relative">
          {/* หน้าแรก */}
          <li>
            <Link to="/" className="hover:text-blue-500 transition-colors">หน้าแรก</Link>
          </li>
          {/* Lessons Dropdown */}
          {/* ... โค้ด dropdown เดิม */}
          {/* Username / UserMenu */}
          {username && (
            <li className="relative">
              <span className="font-semibold text-blue-600 mr-2">{username}</span>
              <button onClick={() => setOpenUserMenu(!openUserMenu)} className="p-2 rounded hover:bg-gray-100">
                <MoreVertical className="w-5 h-5 text-gray-600" />
              </button>
              {openUserMenu && (
                <div className="absolute right-0 mt-2 w-40 bg-white shadow-lg rounded-md border border-gray-200">
                  <button onClick={handleLogout} className="w-full text-left px-4 py-2 text-red-600 hover:bg-red-50">Logout</button>
                </div>
              )}
            </li>
          )}
          {!username && (
            <li>
              <Link
                to="/login"
                state={{ from: location.pathname }}
                className="border-2 border-green-500 text-green-600 px-4 py-2 rounded-lg hover:bg-green-500 hover:text-white"
              >
                Login
              </Link>
            </li>
          )}
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
