import { useEffect, useState, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";

function Testexercise() {
  const { lessonNumber } = useParams();
  const navigate = useNavigate();

  const [lesson, setLesson] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [userAnswers, setUserAnswers] = useState([]);
  const [showAnswers, setShowAnswers] = useState(false);
  const [timeLeft, setTimeLeft] = useState(90);
  const [leaderboard, setLeaderboard] = useState([]);

  // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• user ‡∏à‡∏≤‡∏Å localStorage
  const userObj = JSON.parse(localStorage.getItem("user") || "{}");
  const userId = userObj.user_id; // ‚úÖ ‡∏à‡∏∞‡πÑ‡∏î‡πâ‡πÄ‡∏•‡∏Ç‡∏à‡∏£‡∏¥‡∏á
  const username = userObj.username;
  const token = userObj.token;

  console.log("userId:", userId, "username:", username, "token:", token);



  // Fetch lesson
  useEffect(() => {
    fetch(`http://localhost:3000/exercises/lesson/${lessonNumber}`)
      .then((res) => res.json())
      .then((data) => setLesson(data))
      .catch((err) => console.error("Error fetching lesson:", err));
  }, [lessonNumber]);

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const sec = seconds % 60;
    return `${minutes}:${sec < 10 ? "0" : ""}${sec}`;
  };

  // ----- handleFinish -----
  const handleFinish = useCallback(async () => {
  if (!lesson) return;
  const questions = lesson.questions || [];
  const newUserAnswers = [...userAnswers, selectedOption || "‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å"];

  // ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì score ‡∏ï‡πà‡∏≠‡∏Ç‡πâ‡∏≠
  let finalScore = 0;
  questions.forEach((q, idx) => {
    if (newUserAnswers[idx] === q.correct_answer) finalScore++;
  });

  setUserAnswers(newUserAnswers);
  setScore(finalScore);

  // POST score ‡∏ï‡πà‡∏≠‡∏Ç‡πâ‡∏≠
  try {
    await Promise.all(
