      setCurrentQuestion(currentQuestion + 1);
    } else {
      handleFinish();
    }
  };

  const handleRestart = () => {
    setCurrentQuestion(0);
    setSelectedOption("");
    setScore(0);
    setShowResult(false);
    setUserAnswers([]);
    setShowAnswers(false);
    setTimeLeft(90);
    setLeaderboard([]);
  };

  // ---------------- UI ----------------
  if (showResult) {
    const percentage = Math.round((score / questions.length) * 100);
