import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import heroImage from "../assets/hero.webp";

function UserAuth() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();
  const location = useLocation();
  const from = location.state?.from || "/"; 

  const saveUserData = (data) => {
    localStorage.setItem("user", JSON.stringify({
      user_id: data.user_id,
      username: data.username
    }));
    if (data.token) localStorage.setItem("token", data.token);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setError("กรุณากรอก Email ให้ถูกต้อง");
      setLoading(false);
      return;
    }

    try {
      const res = await fetch("http://localhost:3000/user/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Login failed");

      const saveUserData = (data) => {
  const user = {
    user_id: data.user_id,
    username: data.username,
    email: data.email || "",
    token: data.token || "",
  };
  localStorage.setItem("user", JSON.stringify(user)); // ✅ เก็บ object ทั้งหมด
  // Optional: แยกเก็บเฉพาะค่า token
  if (data.token) localStorage.setItem("token", data.token);
};

      saveUserData(data);
      navigate(from, { replace: true });
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }


  const handleRegister = (e) => {
    e.preventDefault();
    navigate("/register");
  };

  const handleGuestLogin = async () => {
    setError("");
    setLoading(true);
    try {
      const res = await fetch("http://localhost:3000/user/guest", { method: "POST" });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Guest login failed");

      saveUserData(data);
      navigate(from, { replace: true });
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      className="min-h-screen flex items-center justify-center bg-gray-100 bg-cover bg-center relative"
      style={{ backgroundImage: `url(${heroImage})` }}
    >
      <div className="absolute inset-0 bg-black/50"></div>
      <div className="relative z-10 w-full max-w-sm sm:max-w-md bg-white/95 backdrop-blur-md p-10 rounded-3xl shadow-2xl flex flex-col items-center">
        <h2 className="text-3xl sm:text-4xl font-extrabold text-gray-800 mb-8">
          User Access
        </h2>
        {error && <p className="text-red-500 text-sm text-center mb-2">{error}</p>}

        <form className="w-full space-y-5">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full px-5 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full px-5 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
            required
          />

          <button
            onClick={handleLogin}
            disabled={loading}
            className={`w-full py-3 rounded-xl text-white font-semibold flex items-center justify-center ${loading ? "bg-blue-300 cursor-not-allowed" : "bg-blue-600 hover:bg-blue-700"} transition`}
          >
            {loading ? "Logging in..." : "Login"}
          </button>

          <button
            onClick={handleRegister}
            disabled={loading}
            className={`w-full py-3 rounded-xl text-white font-semibold flex items-center justify-center ${loading ? "bg-green-300 cursor-not-allowed" : "bg-green-600 hover:bg-green-700"} transition`}
          >
            {loading ? "Processing..." : "Register"}
          </button>
        </form>

        <button
          onClick={handleGuestLogin}
          disabled={loading}
          className="mt-4 w-full bg-gray-500 text-white py-3 rounded-xl hover:bg-gray-600 transition"
        >
          {loading ? "Loading..." : "Login as Guest"}
        </button>

        <button
          onClick={() => navigate("/")}
          className="mt-6 w-full py-2 text-center rounded-xl border border-gray-400 text-gray-700 hover:bg-gray-100 transition"
        >
          กลับไปหน้า Home
        </button>
      </div>
    </div>
  );
}
};

export default UserAuth;
