import { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import { ChevronDown } from "lucide-react";
import { useAuth } from "../context/AuthContext";

function Navbar() {
  const { username, logout } = useAuth(); // ใช้ username แทน user
  const [lessons, setLessons] = useState([]);
  const [openMenu, setOpenMenu] = useState(null);
  const timeoutRef = useRef(null);

  useEffect(() => {
    fetch("http://localhost:3000/lessons")
      .then((res) => res.json())
      .then((data) => setLessons(data))
      .catch((err) => console.error("Error fetching lessons:", err));
  }, []);

  const handleMouseEnter = (menu) => {
    clearTimeout(timeoutRef.current);
    setOpenMenu(menu);
  };

  const handleMouseLeave = () => {
    timeoutRef.current = setTimeout(() => {
      setOpenMenu(null);
    }, 150);
  };

  const handleLogout = () => {
    logout();
  };

  return (
    <nav className="bg-white shadow-md p-4 sticky top-0 z-50">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-xl font-bold text-blue-600">THAI SIGN</h1>

        <ul className="flex gap-4 items-center relative">
          <li>
            <Link to="/" className="hover:text-blue-500 transition-colors">
              หน้าแรก
            </Link>
          </li>

          {/* เมนูบทเรียน */}
          <li
            className="relative"
            onMouseEnter={() => handleMouseEnter("lessons")}
            onMouseLeave={handleMouseLeave}
          >
            <div className="flex items-center gap-1 cursor-pointer hover:text-blue-500 select-none">
              <Link to="/lessons" className="flex items-center gap-1 hover:text-blue-500">
                <span>บทเรียน</span>
              </Link>
              <ChevronDown
                size={16}
                className={`transition-transform duration-300 ${
                  openMenu === "lessons" ? "rotate-180 text-blue-500" : ""
                }`}
              />
            </div>

            <div
              className={`absolute left-0 mt-2 w-56 bg-white shadow-lg rounded-lg border border-gray-200 transform transition-all duration-300 ease-in-out origin-top z-50 ${
                openMenu === "lessons"
                  ? "scale-y-100 opacity-100"
                  : "scale-y-0 opacity-0 pointer-events-none"
              } max-h-64 overflow-y-auto`}
            >
              <ul>
                {lessons.map((lesson) => (
                  <li key={lesson.lesson_numb|| index}>
                    <Link
                      to={`/lessons/${lesson.number}`}
                      className="block px-4 py-2 hover:bg-blue-50 transition-colors"
                    >
                      บท {lesson.number} : {lesson.title}
                    </Link>
                  </li>
                ))}
              </ul>
            </div>
          </li>

          {/* เมนูแบบฝึกหัด */}
          <li
            className="relative"
            onMouseEnter={() => handleMouseEnter("exercises")}
            onMouseLeave={handleMouseLeave}
          >
            <div className="flex items-center gap-1 cursor-pointer hover:text-blue-500 select-none">
              <Link to="/exercises" className="flex items-center gap-1 hover:text-blue-500">
                <span>แบบฝึกหัด</span>
              </Link>
              <ChevronDown
                size={16}
                className={`transition-transform duration-300 ${
                  openMenu === "exercises" ? "rotate-180 text-blue-500" : ""
                }`}
              />
            </div>

            <div
              className={`absolute left-0 mt-2 w-56 bg-white shadow-lg rounded-lg border border-gray-200 transform transition-all duration-300 ease-in-out origin-top z-50 ${
                openMenu === "exercises"
                  ? "scale-y-100 opacity-100"
                  : "scale-y-0 opacity-0 pointer-events-none"
              } max-h-64 overflow-y-auto`}
            >
              <ul>
                {lessons.map((lesson) => (
                  <li key={lesson.lesson_number}>
                    <Link
                      to={`/exercises/lesson/${lesson.id}`}
                      className="block px-4 py-2 hover:bg-blue-50 transition-colors"
                    >
                      แบบฝึกหัดบทที่ {lesson.number} : {lesson.title}
                    </Link>
                  </li>
                ))}
              </ul>
            </div>
          </li>

          {/* ส่วน Login / User */}
          {username ? (
            <>
              <li className="font-semibold text-blue-600">สวัสดี, {username}</li>
              <li>
                <button
                  onClick={handleLogout}
                  className="px-4 py-2 text-blue-600 border border-blue-600 rounded hover:bg-blue-500 hover:text-white transition-colors"
                >
                  Logout
                </button>
              </li>
            </>
          ) : (
            <>
              <li>
                <Link
                  to="/admin/admin"
                  className="border-2 border-blue-500 text-blue-600 px-4 py-2 rounded-lg hover:bg-blue-500 hover:text-white transition-colors"
                >
                  Admin
                </Link>
              </li>
              <li>
                <Link
                  to="/login"
                  className="border-2 border-green-500 text-green-600 px-4 py-2 rounded-lg hover:bg-green-500 hover:text-white transition-colors"
                >
                  Login
                </Link>
              </li>
            </>
          )}
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
