import { useEffect, useState, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";

function Testexercise() {
  const { lessonNumber } = useParams();
  const navigate = useNavigate();
  const [lesson, setLesson] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [userAnswers, setUserAnswers] = useState([]);
  const [showAnswers, setShowAnswers] = useState(false);
  const [timeLeft, setTimeLeft] = useState(90);
  const [leaderboard, setLeaderboard] = useState([]);

  // üÜï ‡πÄ‡∏û‡∏¥‡πà‡∏° state ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö popup ‡∏ä‡∏∑‡πà‡∏≠
  const [showNamePopup, setShowNamePopup] = useState(false);
  const [username, setUsername] = useState("");

  // Fetch lesson
  useEffect(() => {
    fetch(`http://localhost:3000/exercises/lesson/${lessonNumber}`)
      .then((res) => res.json())
      .then((data) => setLesson(data))
      .catch((err) => console.error("Error:", err));
  }, [lessonNumber]);

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const sec = seconds % 60;
    return `${minutes}:${sec < 10 ? "0" : ""}${sec}`;
  };

  // üß† ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡πÄ‡∏°‡∏∑‡πà‡∏≠‡∏Å‡∏î ‚Äú‡∏™‡∏£‡∏∏‡∏õ‡∏ú‡∏•‚Äù
  const handleFinish = useCallback(() => {
    const answer = selectedOption || "‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å";
    setUserAnswers((prev) => [...prev, answer]);

    const questions = lesson?.questions || [];
    const finalScore =
      score + (answer === questions[currentQuestion]?.answer ? 1 : 0);
    setScore(finalScore);

    // üëâ ‡πÅ‡∏ó‡∏ô prompt ‡∏î‡πâ‡∏ß‡∏¢ popup
    setShowNamePopup(true);
  }, [currentQuestion, selectedOption, score, lesson]);

  // üßæ ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡∏´‡∏•‡∏±‡∏á‡∏Å‡∏£‡∏≠‡∏Å‡∏ä‡∏∑‡πà‡∏≠
  const handleSaveName = () => {
    const key = `lesson_${lessonNumber}_scores`;
    const existing = JSON.parse(localStorage.getItem(key)) || [];
    const newScores = [...existing, { username: username || "‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡πÉ‡∏´‡∏°‡πà", score }];
    localStorage.setItem(key, JSON.stringify(newScores));

    newScores.sort((a, b) => b.score - a.score);
    setLeaderboard(newScores.slice(0, 5));

    setShowNamePopup(false);
    setShowResult(true);
  };

  // Countdown timer
  useEffect(() => {
    if (showResult || showNamePopup) return;
    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timer);
          handleFinish();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    return () => clearInterval(timer);
  }, [showResult, showNamePopup, handleFinish]);

  if (!lesson) return <p className="text-center mt-10">Loading...</p>;

  const questions = lesson.questions;

  const handleNext = () => {
    setUserAnswers((prev) => [...prev, selectedOption || "‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å"]);
    if (selectedOption === questions[currentQuestion].answer)
      setScore(score + 1);

    setSelectedOption("");
    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      handleFinish();
    }
  };

  const handleRestart = () => {
    setCurrentQuestion(0);
    setSelectedOption("");
    setScore(0);
    setShowResult(false);
    setUserAnswers([]);
    setShowAnswers(false);
    setTimeLeft(90);
    setLeaderboard([]);
    setUsername("");
  };

  // üÜï Popup ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏Å‡∏£‡∏≠‡∏Å‡∏ä‡∏∑‡πà‡∏≠
  if (showNamePopup) {
    return (
      <div className="fixed inset-0 flex items-center justify-center bg-black/50 z-50">
        <div className="bg-white rounded-2xl shadow-xl p-8 w-96 text-center">
          <h2 className="text-2xl font-bold mb-4 text-blue-600">
            ‡πÉ‡∏™‡πà‡∏ä‡∏∑‡πà‡∏≠‡∏Ç‡∏≠‡∏á‡∏Ñ‡∏∏‡∏ì‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô
          </h2>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="‡∏Å‡∏£‡∏≠‡∏Å‡∏ä‡∏∑‡πà‡∏≠‡∏Ç‡∏≠‡∏á‡∏Ñ‡∏∏‡∏ì"
            className="border border-gray-300 w-full p-2 rounded-lg mb-4 text-center focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
          <div className="flex justify-center gap-4">
            <button
              onClick={handleSaveName}
              className="px-5 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition"
            >
              ‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô
            </button>
            <button
              onClick={() => {
                setUsername("‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡πÉ‡∏´‡∏°‡πà");
                handleSaveName();
              }}
              className="px-5 py-2 bg-gray-400 text-white rounded-lg hover:bg-gray-500 transition"
            >
              ‡∏Ç‡πâ‡∏≤‡∏°
            </button>
          </div>
        </div>
      </div>
    );
  }

  // ‚úÖ (‡∏™‡πà‡∏ß‡∏ô‡∏≠‡∏∑‡πà‡∏ô ‡πÜ ‡∏Ç‡∏≠‡∏á‡πÇ‡∏Ñ‡πâ‡∏î ‡πÄ‡∏ä‡πà‡∏ô‡∏´‡∏ô‡πâ‡∏≤ Quiz ‡πÅ‡∏•‡∏∞‡∏´‡∏ô‡πâ‡∏≤ Result ‡πÑ‡∏°‡πà‡∏ï‡πâ‡∏≠‡∏á‡πÅ‡∏Å‡πâ)
  // ... (‡πÉ‡∏ä‡πâ‡πÄ‡∏´‡∏°‡∏∑‡∏≠‡∏ô‡∏Ç‡∏≠‡∏á‡∏Ñ‡∏∏‡∏ì‡πÑ‡∏î‡πâ‡πÄ‡∏•‡∏¢)
}
