import { Edit, Trash2 } from "lucide-react";

export default function ContentSection({ title, items }) {
  // จำกัดจำนวนให้โชว์เฉพาะ 5 รายการแรก
  const limitedItems = items.slice(0, 5);

  return (
    <div className="bg-white rounded-xl shadow-md p-5 w-full">
      <div className="flex justify-between items-center mb-3">
        <h3 className="font-semibold text-lg">{title}</h3>
      </div>

      {/* กล่องแสดงรายการ (ถ้ามีมากกว่า 5 จะเลื่อนดูได้) */}
      <div className="max-h-48 overflow-y-auto pr-2">
        <ul className="text-sm space-y-2">
          {limitedItems.map((item, i) => (
            <li key={i} className="flex justify-between items-center">
              <span>{item}</span>
              <div className="flex gap-2">
                <Edit className="w-4 h-4 cursor-pointer text-gray-500 hover:text-blue-500" />
                <Trash2 className="w-4 h-4 cursor-pointer text-gray-500 hover:text-red-500" />
              </div>
            </li>
          ))}
        </ul>
      </div>

      {/* ปุ่ม View All */}
      <button className="mt-4 text-sm text-blue-600 font-medium hover:underline">
        View All {title}
      </button>
    </div>
  );
}
