import { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import { ChevronDown } from "lucide-react"; // ไอคอนลูกศร ▼ (จาก shadcn/lucide-react)

function Navbar() {
  const [lessons, setLessons] = useState([]);
  const [exercises, setExercises] = useState([]);
  const [openMenu, setOpenMenu] = useState(null);
  const timeoutRef = useRef(null);

  useEffect(() => {
    fetch("http://localhost:3000/lessons")
      .then((res) => res.json())
      .then((data) => setLessons(data))
      .catch((err) => console.error("Error fetching lessons:", err));

    fetch("http://localhost:3000/exercises")
      .then((res) => res.json())
      .then((data) => setExercises(data))
      .catch((err) => console.error("Error fetching exercises:", err));
  }, []);

  // ฟังก์ชันควบคุมเปิด/ปิด dropdown อย่างนุ่มนวล
  const handleMouseEnter = (menu) => {
    clearTimeout(timeoutRef.current);
    setOpenMenu(menu);
  };

  const handleMouseLeave = () => {
    timeoutRef.current = setTimeout(() => {
      setOpenMenu(null);
    }, 150); // ดีเลย์ 150ms ป้องกันเมนูหายก่อนคลิก
  };

  return (
    <nav className="bg-white shadow-md p-4 sticky top-0 z-50">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-xl font-bold text-blue-600">THAI SIGN</h1>

        <ul className="flex gap-8 relative">
          <li>
            <Link to="/" className="hover:text-blue-500 transition-colors">
              หน้าแรก
            </Link>
          </li>

          {/* เมนูบทเรียน */}
          <li
            className="relative"
            onMouseEnter={() => handleMouseEnter("lessons")}
            onMouseLeave={handleMouseLeave}
          >
            <div className="flex items-center gap-1 cursor-pointer hover:text-blue-500 select-none">
              <span>บทเรียน</span>
              <ChevronDown
                size={16}
                className={`transition-transform duration-300 ${openMenu === "lessons" ? "rotate-180 text-blue-500" : ""
                  }`}
              />
            </div>

            {/* เมนูย่อย (พร้อม animation และ delay) */}
            <div
              className={`absolute left-0 mt-2 w-56 bg-white shadow-lg rounded-lg border border-gray-200 transform transition-all duration-300 ease-in-out origin-top z-50 ${openMenu === "lessons"
                  ? "scale-y-100 opacity-100"
                  : "scale-y-0 opacity-0 pointer-events-none"
                }`}
            >
              <ul>
                {lessons.map((lesson) => (
                  <li key={lesson.lesson_number}>
                    <Link
                      to={`/lessons/${lesson.number}`}
                      className="block px-4 py-2 hover:bg-blue-50 transition-colors"
                    >
                      บท {lesson.number} : {lesson.title}
                    </Link>
                  </li>
                ))}
              </ul>
            </div>
          </li>

          {/* เมนูแบบฝึกหัด */}
          <li
            className="relative"
            onMouseEnter={() => handleMouseEnter("exercises")}
            onMouseLeave={handleMouseLeave}
          >
            <div className="flex items-center gap-1 cursor-pointer hover:text-blue-500 select-none">
              <span>แบบฝึกหัด</span>
              <ChevronDown
                size={16}
                className={`transition-transform duration-300 ${openMenu === "exercises" ? "rotate-180 text-blue-500" : ""
                  }`}
              />
            </div>

            <div
              className={`absolute left-0 mt-2 w-56 bg-white shadow-lg rounded-lg border border-gray-200 transform transition-all duration-300 ease-in-out origin-top z-50 ${openMenu === "exercises"
                  ? "scale-y-100 opacity-100"
                  : "scale-y-0 opacity-0 pointer-events-none"
                }`}
            >
                <ul>
                {lessons.map((lesson) => (
                  <li key={lesson.lesson_number}>
                    <Link
                      to={`/exercises/${lesson.lesson_number}`}
                      className="block px-4 py-2 hover:bg-blue-50 transition-colors"
                    >
                      แบบฝึกหัดบทที่ {lesson.lesson_number} : {lesson.title}
                    </Link>
                  </li>
                ))}
              </ul>
            </div>
          </li>

          {/* ปุ่ม Admin */}
          <li>
            <Link
              to="/admin"
              className="border-2 border-blue-500 text-blue-600 px-4 py-2 rounded-lg hover:bg-blue-500 hover:text-white transition-colors"
            >
              Admin
            </Link>
          </li>
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
