import { useEffect, useState } from "react";

function Lessons() {
  const [lessons, setLessons] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/api/lessons")
      .then((res) => res.json())
      .then((data) => setLessons(data))
      .catch((err) => console.error("Error:", err));
  }, []);

  return (
    <div className="bg-gray-50 min-h-screen p-6">
      {/* Header */}
      <h1 className="text-3xl font-bold text-center text-gray-800 mb-2">
        บทเรียนทั้งหมด
      </h1>
      <p className="text-center text-gray-600 mb-10">
        บทเรียนทั้งหมด {lessons.length} บท
      </p>

      {/* Lessons Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 max-w-5xl mx-auto">
        {lessons.map((lesson, i) => (
          <div
            key={i}
            className="flex items-start bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition"
          >
            {/* Circle Number */}
            <div className="flex-shrink-0 w-12 h-12 rounded-full bg-blue-600 text-white flex items-center justify-center font-bold text-lg mr-4">
              {i + 1}
            </div>

            {/* Lesson Content */}
            <div>
              <h3 className="font-semibold text-lg text-gray-800">
                {lesson.title}
              </h3>
              <p className="text-gray-600 text-sm mt-2">{lesson.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
