import React, { useState, useEffect } from "react";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

export default function EditSign() {
  const MySwal = withReactContent(Swal);

  // เก็บทุก sign เป็น array
  const [signs, setSigns] = useState([]);

  // fetch ข้อมูลทั้งหมดเมื่อโหลดหน้า
  useEffect(() => {
    fetch(`http://localhost:3000/signs`)
      .then(res => res.json())
      .then(data => {
        const mapped = data.map(s => ({
          sign_id: s.id || s.sign_id,
          word: s.word,
          meaning: s.meaning,
          description: s.description,
          image_file: null,
          image_preview: s.image_data || null
        }));
        setSigns(mapped);
      })
      .catch(err => console.error("Error fetching signs:", err));
  }, []);

  // handle change ของ input (text/textarea)
  const handleSignChange = (e, index) => {
    const { name, value } = e.target;
    setSigns(prev => {
      const newSigns = [...prev];
      newSigns[index][name] = value;
      return newSigns;
    });
  };

  // handle change ของไฟล์
  const handleSignFileChange = (e, index) => {
    const file = e.target.files[0];
    if (file) {
      setSigns(prev => {
        const newSigns = [...prev];
        newSigns[index].image_file = file;
        newSigns[index].image_preview = URL.createObjectURL(file);
        return newSigns;
      });
    }
  };

  // handle submit ของแต่ละ sign
  const handleSubmit = async (e, signId) => {
    e.preventDefault();

    const signToUpdate = signs.find(s => s.sign_id === signId);
    if (!signToUpdate.word) {
      MySwal.fire("ผิดพลาด", "กรุณากรอกคำศัพท์", "error");
      return;
    }

    const result = await MySwal.fire({
      title: "ยืนยันการบันทึก",
      showCancelButton: true,
      confirmButtonText: "ตกลง",
      cancelButtonText: "ยกเลิก"
    });
    if (!result.isConfirmed) return;

    try {
      const formData = new FormData();
      formData.append("word", signToUpdate.word);
      formData.append("meaning", signToUpdate.meaning || "");
      formData.append("description", signToUpdate.description || "");
      if (signToUpdate.image_file) formData.append("image", signToUpdate.image_file);

      const res = await fetch(`http://localhost:3000/signs/${signId}`, {
        method: "PUT",
        body: formData
      });

      if (!res.ok) {
        const text = await res.text();
        throw new Error(`Update failed: ${text}`);
      }

      MySwal.fire("สำเร็จ!", "แก้ไขคำศัพท์เรียบร้อยแล้ว", "success");
    } catch (err) {
      console.error("Error updating sign:", err);
      MySwal.fire("ผิดพลาด", `เกิดข้อผิดพลาดในการแก้ไขคำศัพท์\n${err.message}`, "error");
    }
  };

  return (
    <div className="p-8">
      <h2 className="text-2xl font-bold mb-6">แก้ไขคำศัพท์ทั้งหมด</h2>
      {signs.map((s, index) => (
        <div key={s.sign_id} className="max-w-lg mx-auto p-6 mb-6 bg-white rounded-xl shadow-md">
          <h3 className="text-xl font-semibold mb-3">คำศัพท์ #{s.sign_id}</h3>
          <form onSubmit={(e) => handleSubmit(e, s.sign_id)} className="space-y-3">
            <input
              type="text"
              name="word"
              value={s.word}
              onChange={(e) => handleSignChange(e, index)}
              placeholder="คำศัพท์ (ไทย)"
              className="w-full border px-3 py-2 rounded-md"
            />
            <input
              type="text"
              name="meaning"
              value={s.meaning}
              onChange={(e) => handleSignChange(e, index)}
              placeholder="คำศัพท์ (อังกฤษ)"
              className="w-full border px-3 py-2 rounded-md"
            />
            <textarea
    name="description"
    value={s.description}
    onChange={(e) => handleSignChange(e, index)}
    placeholder="คำอธิบาย"
    className="w-full border px-3 py-2 rounded-md"
  />
            <input
              type="file"
              name="image"
              onChange={(e) => handleSignFileChange(e, index)}
            />
            {s.image_preview && (
              <img src={s.image_preview} alt="Sign" className="mt-2 w-32 h-32 object-cover" />
            )}
            <button type="submit" className="bg-blue-600 text-white px-5 py-2 rounded-md">
              บันทึก
            </button>
          </form>
        </div>
      ))}
    </div>
  );
}
