// src/context/AuthContext.jsx
import { createContext, useContext, useState, useEffect } from "react";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [username, setUsername] = useState(null);
  const [token, setToken] = useState(null);

  // ตรวจสอบ login status เมื่อ reload หน้า
  useEffect(() => {
    if (!token) return;

    fetch("http://localhost:3000/user/check", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.user && data.user.username) setUsername(data.user.username);
      })
      .catch((err) => console.error("Check login error:", err));
  }, [token]);

  const login = async (email, password) => {
    try {
      const res = await fetch("http://localhost:3000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      if (!res.ok) {
        const text = await res.text();
        console.error("Server error:", text);
        return { success: false, message: "Server error" };
      }

      const data = await res.json();
      if (data.username && data.token) {
        setUsername(data.username);
        setToken(data.token);
        return { success: true };
      } else {
        return { success: false, message: data.message || "Login failed" };
      }
    } catch (err) {
      console.error(err);
      return { success: false, message: "Network error" };
    }
  };

  const logout = () => {
    setUsername(null);
    setToken(null);
  };

  return (
    <AuthContext.Provider value={{ username, setUsername, token, setToken, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
