import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

export default function EditSign() {
  const { id } = useParams();
  const navigate = useNavigate();
  const MySwal = withReactContent(Swal);

  const [sign, setSign] = useState({
    lesson_id: "",
    word: "",
    meaning: "",
    description: "",
    video_url: "",
    image_data: null, // สำหรับ preview
  });

  const [imageFile, setImageFile] = useState(null);

  // โหลดข้อมูลเดิม
  useEffect(() => {
    fetch(`http://localhost:3000/signs/id/${id}`)
      .then(res => res.json())
      .then(data => {
        setSign({
          lesson_id: data.lesson_id,
          word: data.word,
          meaning: data.meaning,
          description: data.description,
          video_url: data.video_url,
          image_data: data.image_data
            ? `data:image/jpeg;base64,${btoa(
                String.fromCharCode(...new Uint8Array(data.image_data.data))
              )}`
            : null,
        });
      })
      .catch(err => console.error(err));
  }, [id]);

  // handle input change
  const handleChange = e => {
    const { name, value } = e.target;
    setSign(prev => ({ ...prev, [name]: value }));
  };

  const handleImageChange = e => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      setSign(prev => ({
        ...prev,
        image_data: URL.createObjectURL(file),
      }));
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("lesson_id", sign.lesson_id);
    formData.append("word", sign.word);
    formData.append("meaning", sign.meaning);
    formData.append("description", sign.description);
    formData.append("video_url", sign.video_url);
    if (imageFile) formData.append("image", imageFile);

    try {
      const res = await fetch(`http://localhost:3000/signs/${id}`, {
        method: "PUT",
        body: formData,
      });
      const data = await res.json();
      if (res.ok) {
        MySwal.fire("Success", data.message, "success");
        navigate("/signs"); // กลับไปหน้า list
      } else {
        MySwal.fire("Error", data.message || "Failed to update", "error");
      }
    } catch (err) {
      console.error(err);
      MySwal.fire("Error", "Server error", "error");
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10">
      <h2 className="text-xl font-bold mb-4">Edit Sign</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label>Lesson ID</label>
          <input
            type="number"
            name="lesson_id"
            value={sign.lesson_id}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          />
        </div>
        <div>
          <label>Word</label>
          <input
            type="text"
            name="word"
            value={sign.word}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          />
        </div>
        <div>
          <label>Meaning</label>
          <input
            type="text"
            name="meaning"
            value={sign.meaning}
            onChange={handleChange}
            className="w-full border p-2 rounded"
          />
        </div>
        <div>
          <label>Description</label>
          <textarea
            name="description"
            value={sign.description}
            onChange={handleChange}
            className="w-full border p-2 rounded"
          />
        </div>
        <div>
          <label>Video URL</label>
          <input
            type="text"
            name="video_url"
            value={sign.video_url}
            onChange={handleChange}
            className="w-full border p-2 rounded"
          />
        </div>
        <div>
          <label>Image</label>
          <input type="file" onChange={handleImageChange} />
          {sign.image_data && (
            <img
              src={sign.image_data}
              alt="preview"
              className="mt-2 w-32 h-32 object-cover"
            />
          )}
        </div>
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          Update
        </button>
      </form>
    </div>
  );
}
