import { useEffect, useState, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

function Testexercise() {
  const { lessonNumber } = useParams();
  const navigate = useNavigate();
  const { username, userId } = useAuth(); // ‡∏î‡∏∂‡∏á‡∏ä‡∏∑‡πà‡∏≠‡πÅ‡∏•‡∏∞ userId ‡∏à‡∏≤‡∏Å AuthContext

  const [lesson, setLesson] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [userAnswers, setUserAnswers] = useState([]);
  const [showAnswers, setShowAnswers] = useState(false);
  const [timeLeft, setTimeLeft] = useState(90);
  const [leaderboard, setLeaderboard] = useState([]);

  // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏•‡πá‡∏≠‡∏Å‡∏≠‡∏¥‡∏ô
  useEffect(() => {
    if (!username || !userId) {
      navigate("/login");
      return;
    }

    fetch(`http://localhost:3000/exercises/lesson/${lessonNumber}`)
      .then((res) => res.json())
      .then((data) => setLesson(data))
      .catch((err) => console.error("Error:", err));
  }, [username, userId, lessonNumber, navigate]);

  // Countdown timer
  useEffect(() => {
    if (showResult) return;
    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timer);
          handleFinish();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    return () => clearInterval(timer);
  }, [showResult]);

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const sec = seconds % 60;
    return `${minutes}:${sec < 10 ? "0" : ""}${sec}`;
  };

  const handleNext = () => {
    setUserAnswers((prev) => [...prev, selectedOption || "‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å"]);
    if (selectedOption === lesson.questions[currentQuestion].answer)
      setScore(score + 1);

    setSelectedOption("");
    if (currentQuestion + 1 < lesson.questions.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      handleFinish();
    }
  };

  const handleFinish = useCallback(async () => {
    const answer = selectedOption || "‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å";
    setUserAnswers((prev) => [...prev, answer]);

    const questions = lesson?.questions || [];
    const finalScore =
      score + (answer === questions[currentQuestion]?.answer ? 1 : 0);
    setScore(finalScore);

    // ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡∏•‡∏á DB
    try {
      await fetch("http://localhost:3000/scores", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          user_id: userId,
          lesson_number: lessonNumber,
          exercise_id: lessonNumber, // ‡∏´‡∏£‡∏∑‡∏≠ exercise_id ‡∏à‡∏£‡∏¥‡∏á
          score: finalScore,
        }),
      });
      // ‡πÇ‡∏´‡∏•‡∏î leaderboard
      const lbRes = await fetch(
        `http://localhost:3000/scores?lesson_number=${lessonNumber}`
      );
      const lbData = await lbRes.json();
      lbData.sort((a, b) => b.score - a.score);
      setLeaderboard(lbData.slice(0, 5));
    } catch (err) {
      console.error("Save score error:", err);
    }

    setShowResult(true);
  }, [currentQuestion, selectedOption, score, lesson, userId, lessonNumber]);

  const handleRestart = () => {
    setCurrentQuestion(0);
    setSelectedOption("");
    setScore(0);
    setShowResult(false);
    setUserAnswers([]);
    setShowAnswers(false);
    setTimeLeft(90);
    setLeaderboard([]);
  };

  if (!lesson) return <p className="text-center mt-10">Loading...</p>;

  const question = lesson.questions[currentQuestion];

  // üéØ Result Page
  if (showResult) {
    const percentage = Math.round((score / lesson.questions.length) * 100);
    const isExcellent = percentage >= 80;
    const isGood = percentage >= 50 && percentage < 80;

    return (
      <div className="px-6 py-12 flex justify-center bg-gradient-to-b from-blue-50 to-white min-h-screen">
        <div className="w-full max-w-3xl bg-white rounded-2xl shadow-xl p-8 text-center relative overflow-hidden">
          <h1 className="text-3xl font-bold mb-2">{lesson.title}</h1>
          <p className="text-gray-700 mb-4">‡∏Ñ‡∏∏‡∏ì‡∏ó‡∏≥‡πÅ‡∏ö‡∏ö‡∏ó‡∏î‡∏™‡∏≠‡∏ö‡πÄ‡∏™‡∏£‡πá‡∏à‡πÅ‡∏•‡πâ‡∏ß üéâ</p>

          <div className="bg-gradient-to-r from-green-400 to-blue-500 text-white py-6 rounded-xl shadow-lg mt-6 mb-6">
