              {score} / {lesson.questions.length}
            </h2>
            <p className="text-lg mt-2">‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡∏£‡∏ß‡∏° ({percentage}%)</p>
            <p className="mt-2 text-sm text-blue-100">
              ‡πÄ‡∏ß‡∏•‡∏≤‡∏ó‡∏µ‡πà‡πÉ‡∏ä‡πâ: {formatTime(90 - timeLeft)}
            </p>
          </div>

          <div className="text-5xl mb-4">
            {isExcellent ? "üåü" : isGood ? "üëç" : "üí™"}
          </div>
          <p
            className={`text-lg font-semibold ${
              isExcellent
                ? "text-green-600"
                : isGood
                ? "text-blue-600"
                : "text-red-600"
            }`}
          >
            {isExcellent
              ? "‡∏¢‡∏≠‡∏î‡πÄ‡∏¢‡∏µ‡πà‡∏¢‡∏°‡∏°‡∏≤‡∏Å! ‡∏Ñ‡∏∏‡∏ì‡πÄ‡∏Å‡πà‡∏á‡∏™‡∏∏‡∏î ‡πÜ üëè"
              : isGood
              ? "‡∏ó‡∏≥‡πÑ‡∏î‡πâ‡∏î‡∏µ! ‡∏û‡∏¢‡∏≤‡∏¢‡∏≤‡∏°‡∏≠‡∏µ‡∏Å‡∏ô‡∏¥‡∏î üí™"
              : "‡∏≠‡∏¢‡πà‡∏≤‡∏ó‡πâ‡∏≠ ‡∏•‡∏≠‡∏á‡πÉ‡∏´‡∏°‡πà‡∏≠‡∏µ‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á‡∏ô‡∏∞! üí°"}
          </p>

          {/* Buttons */}
          <div className="flex flex-wrap justify-center gap-4 mt-8">
            <button
              className="px-5 py-2 bg-green-500 text-white rounded-lg shadow hover:bg-green-600 transition"
              onClick={handleRestart}
            >
              ‡∏ó‡∏≥‡∏≠‡∏µ‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á
            </button>
            <button
              className="px-5 py-2 bg-blue-500 text-white rounded-lg shadow hover:bg-blue-600 transition"
              onClick={() => setShowAnswers(true)}
            >
              ‡∏ï‡∏£‡∏ß‡∏à‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö
            </button>
            <button
              className="px-5 py-2 bg-gray-500 text-white rounded-lg shadow hover:bg-gray-600 transition"
              onClick={() => navigate(-1)}
            >
              ‡∏Å‡∏•‡∏±‡∏ö‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤‡∏Å‡πà‡∏≠‡∏ô
            </button>
          </div>

          {/* ‚úÖ ‡πÄ‡∏â‡∏•‡∏¢ */}
          {showAnswers && (
            <div className="mt-10 text-left">
              <h2 className="text-xl font-bold mb-3 text-center text-blue-600">
                ‡∏ï‡∏£‡∏ß‡∏à‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö‡∏Ç‡∏≠‡∏á‡∏Ñ‡∏∏‡∏ì
              </h2>
              {lesson.questions.map((q, idx) => {
                const userAnswer = userAnswers[idx] || "‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å";
                const isCorrect = userAnswer === q.answer;
                return (
                  <div
                    key={idx}
                    className={`p-4 mb-3 rounded-lg border transition ${
                      isCorrect
                        ? "bg-green-50 border-green-400"
                        : "bg-red-50 border-red-400"
                    }`}
                  >
                    <p className="font-semibold text-gray-800">
                      {idx + 1}. {q.question}
                    </p>
                    <p className="mt-1">
                      ‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö‡∏Ç‡∏≠‡∏á‡∏Ñ‡∏∏‡∏ì:{" "}
                      <span
                        className={`px-2 py-1 rounded ${
                          isCorrect
                            ? "bg-green-200 text-green-800"
                            : "bg-red-200 text-red-800"
                        }`}
                      >
                        {userAnswer}
                      </span>
                    </p>
                    {!isCorrect && (
                      <p className="mt-1 text-green-700">
                        ‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö‡∏ó‡∏µ‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á: {q.answer}
                      </p>
                    )}
                  </div>
                );
              })}
            </div>
          )}

          {/* üèÜ Leaderboard */}
          {leaderboard && leaderboard.length > 0 && (
            <div className="mt-10 bg-gradient-to-r from-yellow-100 to-yellow-50 p-6 rounded-xl shadow">
              <h2 className="text-xl font-bold mb-4 text-center text-yellow-700">
                üèÜ Leaderboard ‡∏≠‡∏±‡∏ô‡∏î‡∏±‡∏ö‡∏™‡∏π‡∏á‡∏™‡∏∏‡∏î
              </h2>
              <ol className="list-decimal ml-6 text-gray-800">
                {leaderboard.map((item, idx) => (
                  <li
                    key={idx}
                    className={`mb-2 ${
                      item.user_id === userId ? "font-bold text-green-600" : ""
                    }`}
                  >
                    {idx + 1}. {item.username} - {item.score} ‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô{" "}
                    {item.user_id === userId && "‚≠ê (‡∏Ñ‡∏∏‡∏ì)"}
                  </li>
                ))}
              </ol>
            </div>
          )}
        </div>
      </div>
    );
  }

  // Quiz Page
  return (
    <div className="px-4 py-12 flex justify-center">
      <div className="w-full max-w-3xl">
        <div className="mb-4 text-center">
          <h1 className="text-3xl font-bold">{lesson.title}</h1>
          <p className="text-gray-600 mt-1">
            ‡∏Ç‡πâ‡∏≠ {currentQuestion + 1} / {lesson.questions.length}
          </p>

          {/* Progress bar */}
          <div className="w-full bg-gray-200 h-2 rounded-full mt-2">
            <div
              className="h-2 rounded-full bg-green-500 transition-all duration-300"
              style={{
                width: `${((currentQuestion + 1) / lesson.questions.length) * 100}%`,
              }}
            ></div>
          </div>

          {/* Timer */}
          <p
            className={`mt-2 font-semibold ${
              timeLeft <= 10 ? "text-red-500" : "text-green-700"
            }`}
          >
            ‡πÄ‡∏ß‡∏•‡∏≤‡πÄ‡∏´‡∏•‡∏∑‡∏≠: {formatTime(timeLeft)}
          </p>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-lg transition hover:shadow-xl">
          <h3 className="text-lg font-semibold mb-4">{question.question}</h3>

          {question.image_data && (
            <div className="flex justify-center mb-4">
              <img
                src={`data:image/jpeg;base64,${question.image_data}`}
                alt="question"
                className="w-40 h-40 object-contain rounded"
              />
            </div>
          )}

          <div className="flex flex-col gap-3">
            {question.options.map((opt, idx) => (
              <button
                key={idx}
                className={`p-3 rounded-lg border text-left transition ${
                  selectedOption === opt
                    ? "bg-blue-500 text-white shadow"
                    : "bg-white hover:bg-blue-50"
                }`}
                onClick={() => setSelectedOption(opt)}
              >
                {opt}
              </button>
            ))}
          </div>

          <div className="flex justify-center gap-4 mt-6">
            <button
              className="px-6 py-2 bg-gray-400 text-white rounded shadow hover:bg-gray-500 disabled:opacity-50"
              onClick={() => {
                if (currentQuestion > 0) {
                  setCurrentQuestion(currentQuestion - 1);
                  setSelectedOption(userAnswers[currentQuestion - 1] || "");
                }
              }}
              disabled={currentQuestion === 0}
            >
              ‡∏¢‡πâ‡∏≠‡∏ô‡∏Å‡∏•‡∏±‡∏ö
            </button>

            <button
              className="px-6 py-2 bg-green-500 text-white rounded shadow hover:bg-green-600 disabled:opacity-50"
              onClick={handleNext}
              disabled={!selectedOption}
            >
              {currentQuestion + 1 < lesson.questions.length
                ? "‡∏ñ‡∏±‡∏î‡πÑ‡∏õ"
                : "‡∏™‡∏£‡∏∏‡∏õ‡∏ú‡∏•"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

