import { useEffect, useState, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";

function Testexercise() {
  const { lessonNumber } = useParams();
  const navigate = useNavigate();

  const [lesson, setLesson] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [userAnswers, setUserAnswers] = useState([]);
  const [showAnswers, setShowAnswers] = useState(false);
  const [timeLeft, setTimeLeft] = useState(90);
  const [leaderboard, setLeaderboard] = useState([]);

  // ‚úÖ ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• user ‡∏à‡∏≤‡∏Å localStorage ‡πÅ‡∏•‡∏∞ fallback ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ user_id
  // ‡πÅ‡∏ó‡∏ô
const storedUser = JSON.parse(localStorage.getItem("user") || "{}");
const userId = storedUser.user_id || 9999;
const username = storedUser.username || "Guest";
const token = storedUser.token || "";


  if (!userId) {
    console.warn("‚ö† ‡πÑ‡∏°‡πà‡∏°‡∏µ user_id ‡πÉ‡∏ô localStorage ‚Äî ‡πÉ‡∏ä‡πâ Guest Mode");
    userId = 9999; // ‡∏Ñ‡πà‡∏≤ fallback ‡∏õ‡∏•‡∏≠‡∏î‡∏†‡∏±‡∏¢
    username = "Guest";
  }

  console.log("üë§ User loaded:", { userId, username, token });

  // üß† ‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ö‡∏ó‡πÄ‡∏£‡∏µ‡∏¢‡∏ô
  useEffect(() => {
    fetch(`http://localhost:3000/exercises/lesson/${lessonNumber}`)
      .then((res) => res.json())
      .then((data) => setLesson(data))
      .catch((err) => console.error("Error fetching lesson:", err));
  }, [lessonNumber]);

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const sec = seconds % 60;
    return `${minutes}:${sec < 10 ? "0" : ""}${sec}`;
  };

  // ‚úÖ handleFinish
  const handleFinish = useCallback(async () => {
    if (!lesson) return;
    console.log("üöÄ handleFinish ‡∏ñ‡∏π‡∏Å‡πÄ‡∏£‡∏µ‡∏¢‡∏Å‡πÅ‡∏•‡πâ‡∏ß!");

    const questions = lesson.questions || [];
    const answer = selectedOption || "‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å";
    const newUserAnswers = [...userAnswers, answer];

    let finalScore = 0;
    questions.forEach((q, idx) => {
      if (newUserAnswers[idx] === q.answer) finalScore++;
    });

    setUserAnswers(newUserAnswers);
    setScore(finalScore);

    // ‚úÖ POST score ‡∏ñ‡πâ‡∏≤‡∏°‡∏µ token
    try {
      console.log("üì° POST /scores", { userId, username, finalScore });

      const res = await fetch("http://localhost:3000/scores", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          user_id: userId,
          username: username,
          lesson_number: Number(lessonNumber),
          exercise_id: lesson.exercise_id,
          score: finalScore,
        }),
      });

      const data = await res.json();
      console.log("‚úÖ POST /scores response:", data);
    } catch (err) {
      console.error("‚ùå Save score error:", err);
    }

    // ‚úÖ ‡πÇ‡∏´‡∏•‡∏î leaderboard
    try {
      const lbRes = await fetch(
        `http://localhost:3000/scores?lesson_number=${lessonNumber}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const lbData = await lbRes.json();
      lbData.sort((a, b) => b.score - a.score);
      setLeaderboard(lbData.slice(0, 5));
    } catch (err) {
      console.error("‚ùå Load leaderboard error:", err);
    }

    setShowResult(true);
  }, [lesson, selectedOption, userAnswers, userId, token, lessonNumber, username]);

  // ‚è≥ Timer
  useEffect(() => {
    if (showResult) return;
    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timer);
          handleFinish();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    return () => clearInterval(timer);
  }, [showResult, handleFinish]);

  if (!lesson) return <p className="text-center mt-10">Loading...</p>;

  const questions = lesson.questions;
  const question = questions[currentQuestion];

  const handleNext = () => {
    const answer = selectedOption || "‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å";
    setUserAnswers((prev) => [...prev, answer]);
    setSelectedOption("");
    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      handleFinish();
    }
  };

  const handleRestart = () => {
    setCurrentQuestion(0);
    setSelectedOption("");
    setScore(0);
    setShowResult(false);
    setUserAnswers([]);
    setShowAnswers(false);
    setTimeLeft(90);
    setLeaderboard([]);
  };

  // ---------------- UI ----------------
  if (showResult) {
    const percentage = Math.round((score / questions.length) * 100);
    const isExcellent = percentage >= 80;
    const isGood = percentage >= 50 && percentage < 80;

    return (
      <div className="px-6 py-12 flex justify-center bg-gradient-to-b from-blue-50 to-white min-h-screen">
        <div className="w-full max-w-3xl bg-white rounded-2xl shadow-xl p-8 text-center relative overflow-hidden">
          <h1 className="text-3xl font-bold mb-2">{lesson.title}</h1>
          <p className="text-gray-700 mb-4">‡∏Ñ‡∏∏‡∏ì‡∏ó‡∏≥‡πÅ‡∏ö‡∏ö‡∏ó‡∏î‡∏™‡∏≠‡∏ö‡πÄ‡∏™‡∏£‡πá‡∏à‡πÅ‡∏•‡πâ‡∏ß üéâ</p>

          <div className="bg-gradient-to-r from-green-400 to-blue-500 text-white py-6 rounded-xl shadow-lg mt-6 mb-6">
            <h2 className="text-6xl font-extrabold">
              {score} / {questions.length}
            </h2>
            <p className="text-lg mt-2">‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡∏£‡∏ß‡∏° ({percentage}%)</p>
            <p className="mt-2 text-sm text-blue-100">
              ‡πÄ‡∏ß‡∏•‡∏≤: {formatTime(90 - timeLeft)}
            </p>
          </div>

          <p
            className={`text-lg font-semibold ${isExcellent
              ? "text-green-600"
              : isGood
              ? "text-blue-600"
              : "text-red-600"
            }`}
          >
            {isExcellent
              ? "‡∏¢‡∏≠‡∏î‡πÄ‡∏¢‡∏µ‡πà‡∏¢‡∏°‡∏°‡∏≤‡∏Å! üëè"
              : isGood
              ? "‡∏ó‡∏≥‡πÑ‡∏î‡πâ‡∏î‡∏µ! üí™"
              : "‡∏≠‡∏¢‡πà‡∏≤‡∏ó‡πâ‡∏≠ ‡∏•‡∏≠‡∏á‡πÉ‡∏´‡∏°‡πà‡∏≠‡∏µ‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á‡∏ô‡∏∞!"}
          </p>

          <div className="flex flex-wrap justify-center gap-4 mt-8">
            <button className="px-5 py-2 bg-green-500 text-white rounded-lg shadow hover:bg-green-600 transition" onClick={handleRestart}>
              ‡∏ó‡∏≥‡∏≠‡∏µ‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á
            </button>
            <button className="px-5 py-2 bg-blue-500 text-white rounded-lg shadow hover:bg-blue-600 transition" onClick={() => setShowAnswers(true)}>
              ‡∏ï‡∏£‡∏ß‡∏à‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö
            </button>
            <button className="px-5 py-2 bg-gray-500 text-white rounded-lg shadow hover:bg-gray-600 transition" onClick={() => navigate(-1)}>
              ‡∏Å‡∏•‡∏±‡∏ö‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤‡∏Å‡πà‡∏≠‡∏ô
            </button>
          </div>

          {showAnswers && (
            <div className="mt-10 text-left">
              <h2 className="text-xl font-bold mb-3 text-center text-blue-600">‡∏ï‡∏£‡∏ß‡∏à‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö‡∏Ç‡∏≠‡∏á‡∏Ñ‡∏∏‡∏ì</h2>
              {questions.map((q, idx) => {
                const userAnswer = userAnswers[idx] || "‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å";
                const isCorrect = userAnswer === q.answer;
                return (
                  <div key={idx} className={`p-4 mb-3 rounded-lg border ${isCorrect ? "bg-green-50 border-green-400" : "bg-red-50 border-red-400"}`}>
                    <p className="font-semibold text-gray-800">{idx + 1}. {q.question}</p>
                    <p className="mt-1">
                      ‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö‡∏Ç‡∏≠‡∏á‡∏Ñ‡∏∏‡∏ì:{" "}
                      <span className={`px-2 py-1 rounded ${isCorrect ? "bg-green-200 text-green-800" : "bg-red-200 text-red-800"}`}>
                        {userAnswer}
                      </span>
                    </p>
                    {!isCorrect && <p className="mt-1 text-green-700">‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö‡∏ó‡∏µ‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á: {q.answer}</p>}
                  </div>
                );
              })}
            </div>
          )}

          {leaderboard.length > 0 && (
            <div className="mt-10 bg-gradient-to-r from-yellow-100 to-yellow-50 p-6 rounded-xl shadow">
              <h2 className="text-xl font-bold mb-4 text-center text-yellow-700">üèÜ Leaderboard</h2>
              <ol className="list-decimal ml-6 text-gray-800">
                {leaderboard.map((item, idx) => (
                  <li key={idx} className={`${item.user_id === userId ? "font-bold text-green-600" : ""}`}>
                    {idx + 1}. {item.username} - {item.score} ‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô {item.user_id === userId && "‚≠ê (‡∏Ñ‡∏∏‡∏ì)"}
                  </li>
                ))}
              </ol>
            </div>
          )}
        </div>
      </div>
    );
  }

  // ---- UI ‡∏ó‡∏≥‡πÅ‡∏ö‡∏ö‡∏ù‡∏∂‡∏Å‡∏´‡∏±‡∏î ----
  return (
    <div className="px-4 py-12 flex justify-center">
      <div className="w-full max-w-3xl">
        <div className="mb-4 text-center">
          <h1 className="text-3xl font-bold">{lesson.title}</h1>
          <p className="text-gray-600 mt-1">
            ‡∏Ç‡πâ‡∏≠ {currentQuestion + 1} / {questions.length}
          </p>
          <p className="mt-2 font-semibold text-green-700">
            ‡πÄ‡∏ß‡∏•‡∏≤‡πÄ‡∏´‡∏•‡∏∑‡∏≠: {formatTime(timeLeft)}
          </p>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-lg transition hover:shadow-xl">
          <h3 className="text-lg font-semibold mb-4">{question.question}</h3>
          {question.image_data && (
            <div className="flex justify-center mb-4">
              <img src={`data:image/jpeg;base64,${question.image_data}`} alt="question" className="w-40 h-40 object-contain rounded" />
            </div>
          )}
          <div className="flex flex-col gap-3">
            {question.options.map((opt, idx) => (
              <button key={idx} className={`p-3 rounded-lg border text-left ${selectedOption === opt ? "bg-blue-500 text-white shadow" : "bg-white hover:bg-blue-50"}`} onClick={() => setSelectedOption(opt)}>
                {opt}
              </button>
            ))}
          </div>

          <div className="flex justify-center gap-4 mt-6">
            <button className="px-6 py-2 bg-gray-400 text-white rounded shadow hover:bg-gray-500 disabled:opacity-50" onClick={() => currentQuestion > 0 && setCurrentQuestion(currentQuestion - 1)} disabled={currentQuestion === 0}>
              ‡∏¢‡πâ‡∏≠‡∏ô‡∏Å‡∏•‡∏±‡∏ö
            </button>
            <button className="px-6 py-2 bg-green-500 text-white rounded shadow hover:bg-green-600" onClick={handleNext}>
              {currentQuestion + 1 < questions.length ? "‡∏ñ‡∏±‡∏î‡πÑ‡∏õ" : "‡∏™‡∏£‡∏∏‡∏õ‡∏ú‡∏•"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Testexercise;
