// src/components/Navbar.js
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

function Navbar() {
  const { user, setUser, token } = useAuth();
  const [lessons, setLessons] = useState([]);

  // โหลดบทเรียน
  useEffect(() => {
    fetch("http://localhost:3000/lessons")
      .then((res) => res.json())
      .then((data) => setLessons(data))
      .catch((err) => console.error("Error fetching lessons:", err));
  }, []);

  // โหลดข้อมูล user จาก backend ผ่าน /check
  useEffect(() => {
    if (!token) return;

    fetch("http://localhost:3000/user/check", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.user) setUser(data.user); // อัปเดต context user
      })
      .catch((err) => console.error("Error fetching user:", err));
  }, [token, setUser]);

  return (
    <nav className="bg-white shadow-md p-4 sticky top-0 z-50">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-xl font-bold text-blue-600">THAI SIGN</h1>

        <ul className="flex gap-8 items-center">
          <li>
            <Link to="/" className="hover:text-blue-500 transition-colors">
              หน้าแรก
            </Link>
          </li>

          <li>
            <Link to="/lessons" className="hover:text-blue-500 transition-colors">
              บทเรียน
            </Link>
          </li>

          <li>
            <Link to="/exercises" className="hover:text-blue-500 transition-colors">
              แบบฝึกหัด
            </Link>
          </li>

          {/* แสดง username ถ้า login */}
          {user && (
            <li className="font-semibold text-blue-600">
              สวัสดี, {user.username}
            </li>
          )}
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
