// src/user/Register.js
import { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

export default function Register() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleRegister = async () => {
    try {
      const res = await axios.post("http://localhost:3000/user/register", {
        username,
        email,
        password,
      });

      // ถ้า backend ส่ง token กลับมา
      if (res.data.token) {
        login({ user_id: res.data.user_id }, res.data.token);
        navigate("/exercises"); // ไปหน้าฝึกหัด
      } else {      
        setMessage("Registration failed. Please try again.");
      }
    } catch (err) {
      setMessage(err.response?.data?.message || err.message);
    }
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-50">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
        <h1 className="text-2xl font-bold mb-6 text-center">สมัครสมาชิก</h1>

        <div className="flex flex-col gap-4">
          <input
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <input
            placeholder="Email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <input
            placeholder="Password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            onClick={handleRegister}
            className="bg-blue-600 text-white p-3 rounded-md hover:bg-blue-700 transition"
          >
            สมัครสมาชิก
          </button>
        </div>

        {message && (
          <p className="mt-4 text-center text-red-500">{message}</p>
        )}

        <p className="mt-4 text-center text-sm text-gray-600">
          มีบัญชีอยู่แล้ว?{" "}
          <Link to="/login" className="text-blue-600 hover:underline">
            เข้าสู่ระบบ
          </Link>
        </p>
      </div>
    </div>
  );
}
