import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Users, Barcode } from "lucide-react";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";
import { Edit, Trash2 } from "lucide-react";
import Card from "../components-Dashboard/Card";
import ContentSection from "../components-Dashboard/ContentSection";
import AdminTable from "../components-Dashboard/AdminTable";
import Header from "../components-Dashboard/Header";
import Sidebar from "../components-Dashboard/Sidebar";

export default function Dashboard() {
  const navigate = useNavigate();
  const MySwal = withReactContent(Swal);

  // State
  const [lessons, setLessons] = useState([]);
  const [words, setWords] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [admins, setAdmins] = useState([]);
  const [visits, setVisits] = useState(0);
  const [contentCount, setContentCount] = useState(0);

  // =============================
  // üîπ ‡∏õ‡∏∏‡πà‡∏° +Add Popup Menu
  // =============================
  const handleAddClick = () => {
    MySwal.fire({
      title: "‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡πÄ‡∏û‡∏¥‡πà‡∏°",
      showConfirmButton: false,
      showCloseButton: true,
      focusConfirm: false,
      html: `
        <div class="flex flex-col gap-3 text-left">
          <button id="add-lesson" class="swal2-styled w-full mb-1">‚ûï ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ö‡∏ó‡πÄ‡∏£‡∏µ‡∏¢‡∏ô (Lesson)</button>
          <button id="add-sign" class="swal2-styled w-full mb-1">‚úã ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Ñ‡∏≥‡∏®‡∏±‡∏û‡∏ó‡πå (Sign)</button>
          <button id="add-exercise" class="swal2-styled w-full mb-1">üß© ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Ñ‡∏≥‡∏ñ‡∏≤‡∏° (Exercise)</button>
          <button id="add-admin" class="swal2-styled w-full mb-1">üë§ ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ú‡∏π‡πâ‡∏î‡∏π‡πÅ‡∏•‡∏£‡∏∞‡∏ö‡∏ö (Admin)</button>
        </div>
      `,
      didOpen: () => {
        document.getElementById("add-lesson")?.addEventListener("click", () => {
          MySwal.close();
          navigate("/add-lesson");
        });
        document.getElementById("add-sign")?.addEventListener("click", () => {
          MySwal.close();
          navigate("/add-sign");
        });
        document.getElementById("add-exercise")?.addEventListener("click", () => {
          MySwal.close();
          navigate("/add-exercise");
        });
        document.getElementById("add-admin")?.addEventListener("click", () => {
          MySwal.close();
          navigate("/add-admin");
        });
      },
    });
  };

  // =============================
  // üîπ useEffect ‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•
  // =============================
  useEffect(() => {
    fetchLessons();
    fetchWords();
    fetchQuestions();
    fetchAdmins();

    // ‡∏ô‡∏±‡∏ö‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏ú‡∏π‡πâ‡πÄ‡∏Ç‡πâ‡∏≤‡∏ä‡∏° (localStorage)
    const currentVisits = parseInt(localStorage.getItem("visits") || "0") + 1;
    localStorage.setItem("visits", currentVisits);
    setVisits(currentVisits);
  }, []);

  useEffect(() => {
    // ‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡πÄ‡∏ô‡∏∑‡πâ‡∏≠‡∏´‡∏≤‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î
    setContentCount(lessons.length + words.length + questions.length);
  }, [lessons, words, questions]);

  // =============================
  // üîπ ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≤‡∏Å Backend
  // =============================
  const fetchLessons = async () => {
    try {
      const res = await fetch("http://localhost:3000/lessons");
      const data = await res.json();
      setLessons(data.map((l) => ({ ...l, lesson_id: l.id || l.lesson_id })));
    } catch (err) {
      console.error("Error fetching lessons:", err);
    }
  };

  const fetchWords = async () => {
    try {
      const res = await fetch("http://localhost:3000/signs");
      const data = await res.json();
      setWords(data.map((w) => ({ ...w, sign_id: w.id || w.sign_id })));
    } catch (err) {
      console.error("Error fetching signs:", err);
    }
  };

  const fetchQuestions = async () => {
    try {
      const res = await fetch("http://localhost:3000/exercises");
      const data = await res.json();
      setQuestions(data.map((q) => ({ ...q, exercise_id: q.id || q.exercise_id })));
    } catch (err) {
      console.error("Error fetching exercises:", err);
    }
  };

  const fetchAdmins = async () => {
    try {
      const res = await fetch("http://localhost:3000/admins");
      const data = await res.json();
      setAdmins(data.map((a) => ({ ...a, admin_id: a.id || a.admin_id })));
    } catch (err) {
      console.error("Error fetching admins:", err);
    }
  };

  // =============================
  // üîπ UI Layout
  // =============================
  return (
    <div className="flex min-h-screen bg-gray-50">
      <Sidebar />

      <div className="flex-1 p-8">
        <Header />
        <h1 className="text-3xl font-bold text-gray-800 mb-8">üìä Dashboard</h1>

        {/* Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-10">
          <Card title="Total Users" value={visits} icon={Users} />
          <Card title="Content Items" value={contentCount} icon={Barcode} />
        </div>

        {/* Content Management */}
        <section>
          <h2 className="text-2xl font-semibold mb-4 text-gray-700">Content Management</h2>

          <div className="flex justify-between items-center mb-6">
            <input
              type="text"
              placeholder="Search..."
              className="border border-gray-300 rounded-md px-3 py-2 text-sm w-1/2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <button
              onClick={handleAddClick}
              className="bg-blue-600 text-white px-5 py-2 rounded-md hover:bg-blue-700 transition"
            >
              + Add
            </button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {/* ‡∏ö‡∏ó‡πÄ‡∏£‡∏µ‡∏¢‡∏ô */}
            <ContentSection
              title="‡∏ö‡∏ó‡πÄ‡∏£‡∏µ‡∏¢‡∏ô"
              items={lessons}
              onDelete={async (lesson) => {
                if (window.confirm("‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö?")) {
                  await fetch(`http://localhost:3000/lessons/${lesson.lesson_id}`, { method: "DELETE" });
                  fetchLessons();
                }
              }}
            />

            {/* ‡∏Ñ‡∏≥‡∏®‡∏±‡∏û‡∏ó‡πå */}
            <ContentSection
              title="‡∏Ñ‡∏≥‡∏®‡∏±‡∏û‡∏ó‡πå"
              items={words}
              onEdit={(word) => navigate(`/edit-word/${word.sign_id}`)}
              onDelete={async (word) => {
                if (window.confirm("‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö?")) {
                  await fetch(`http://localhost:3000/signs/${word.sign_id}`, { method: "DELETE" });
                  fetchWords();
                }
              }}
            />

            {/* ‡∏Ñ‡∏≥‡∏ñ‡∏≤‡∏° */}
            <ContentSection
              title="‡∏Ñ‡∏≥‡∏ñ‡∏≤‡∏°"
              items={questions}
              onEdit={(q) => navigate(`/edit-exercise/${q.exercise_id}`)}
              onDelete={async (q) => {
                if (window.confirm("‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö?")) {
                  await fetch(`http://localhost:3000/exercises/${q.exercise_id}`, { method: "DELETE" });
                  fetchQuestions();
                }
              }}
            />
          </div>
        </section>

        {/* Admin Management */}
        <section className="mt-10">
          <h2 className="text-2xl font-semibold mb-4 text-gray-700">Admin Management</h2>
          <AdminTable data={admins} />
        </section>
      </div>
    </div>
  );
}
