import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

export default function EditLesson() {
  const { id } = useParams();
  const navigate = useNavigate();
  const MySwal = withReactContent(Swal);

  const [lesson, setLesson] = useState({
    lesson_id: "",
    lesson_number: "",
    lesson_title: "",
    description: "",
    image_data: null
  });

  // โหลดข้อมูลบทเรียนตาม id
  useEffect(() => {
    fetch(`http://localhost:3000/lessons/${id}`)
      .then(res => res.json())
      .then(data => {
        setLesson({
          lesson_id: data.lesson_id,
          lesson_number: data.lesson_number,
          lesson_title: data.lesson_title,
          description: data.description,
          image_data: data.image_data || null
        });
      })
      .catch(err => console.error("Error fetching lesson:", err));
  }, [id]);

  // อัปเดต state เมื่อเปลี่ยน input
  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (name === "image" && files && files.length > 0) {
      const file = files[0];

      // ตรวจสอบขนาดไฟล์ (5MB)
      const maxSize = 5 * 1024 * 1024;
      if (file.size > maxSize) {
        MySwal.fire("ผิดพลาด", "ขนาดไฟล์เกิน 5MB", "error");
        return;
      }

      const reader = new FileReader();
      reader.onloadend = () => setLesson(prev => ({ ...prev, image_data: reader.result }));
      reader.readAsDataURL(file);
    } else {
      setLesson(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const result = await MySwal.fire({
      title: "ยืนยันการบันทึก",
      showCancelButton: true,
      confirmButtonText: "ตกลง",
      cancelButtonText: "ยกเลิก"
    });
    if (!result.isConfirmed) return;

    try {
      // แปลง lesson_number เป็น int
      const payload = {
        lesson_number: parseInt(lesson.lesson_number, 10),
        lesson_title: lesson.lesson_title,
        description: lesson.description,
        image_data: lesson.image_data
      };

      const res = await fetch(`http://localhost:3000/lessons/${lesson.lesson_id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload)
      });

      if (!res.ok) {
        const text = await res.text();
        throw new Error(`Update failed: ${text}`);
      }

      MySwal.fire("สำเร็จ!", "แก้ไขบทเรียนเรียบร้อยแล้ว", "success");
      navigate("/dashboard");
    } catch (err) {
      console.error("Error updating lesson:", err);
      MySwal.fire("ผิดพลาด", `เกิดข้อผิดพลาดในการแก้ไขบทเรียน\n${err.message}`, "error");
    }
  };

  return (
    <div className="max-w-lg mx-auto p-8 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-6">แก้ไขบทเรียน</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="lesson_number"
          value={lesson.lesson_number}
          onChange={handleChange}
          placeholder="Lesson Number"
          className="w-full border px-3 py-2 rounded-md"
        />
        <input
          type="text"
          name="lesson_title"
          value={lesson.lesson_title}
          onChange={handleChange}
          placeholder="Title"
          className="w-full border px-3 py-2 rounded-md"
        />
        <textarea
          name="description"
          value={lesson.description}
          onChange={handleChange}
          placeholder="Description"
          className="w-full border px-3 py-2 rounded-md"
        />
        <input type="file" name="image" accept="image/*" onChange={handleChange} />
        {lesson.image_data && (
          <img
            src={lesson.image_data}
            alt="Lesson"
            className="mt-2 w-32 h-32 object-cover"
          />
        )}
        <button
          type="submit"
          className="bg-blue-600 text-white px-5 py-2 rounded-md"
        >
          บันทึก
        </button>
      </form>
    </div>
  );
}
