  const [lessons, setLessons] = useState([]);

  useEffect(() => {
    // ‚úÖ ‡∏ï‡∏£‡∏ß‡∏à‡πÉ‡∏´‡πâ‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏ß‡πà‡∏≤‡πÄ‡∏£‡∏µ‡∏¢‡∏Å API ‡∏à‡∏≤‡∏Å backend (‡πÄ‡∏ä‡πà‡∏ô http://localhost:5000/api/lessons)
    fetch("http://localhost:5000/lessons")
      .then((res) => res.json())
      .then((data) => setLessons(data))
      .catch((err) => console.error("Error fetching lessons:", err));
  }, []);

  return (
    <div className="px-6 py-12">
      <h1 className="text-3xl font-bold">üìò ‡∏ö‡∏ó‡πÄ‡∏£‡∏µ‡∏¢‡∏ô‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î</h1>
      <p className="text-gray-600 mt-2">
        ‡∏ö‡∏ó‡πÄ‡∏£‡∏µ‡∏¢‡∏ô‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î {lessons.length} ‡∏ö‡∏ó
      </p>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-8">
        {lessons.map((lesson, index) => (
          <Link
            to={`/lessons/${lesson.id}`}
            key={lesson.id || index}
            className="flex bg-white border border-gray-200 rounded-lg shadow-sm hover:shadow-md transition p-4 gap-4"
          >
            {/* ‚úÖ ‡πÅ‡∏™‡∏î‡∏á‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û‡∏ö‡∏ó‡πÄ‡∏£‡∏µ‡∏¢‡∏ô */}
            {lesson.image_data ? (
              <img
                src={lesson.image_data}
                alt={lesson.title}
                className="w-32 h-32 object-cover rounded-md"
              />
            ) : (
              <div className="w-32 h-32 flex items-center justify-center bg-gray-200 text-gray-500 text-sm rounded-md">
                ‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏£‡∏π‡∏õ
              </div>
            )}

            {/* ‚úÖ ‡πÄ‡∏ô‡∏∑‡πâ‡∏≠‡∏´‡∏≤‡∏ö‡∏ó‡πÄ‡∏£‡∏µ‡∏¢‡∏ô */}
            <div className="flex flex-col justify-center flex-1">
              <h3 className="font-semibold text-lg text-blue-700">
                ‡∏ö‡∏ó‡∏ó‡∏µ‡πà {lesson.number || index + 1}: {lesson.title}
              </h3>
              <p className="text-gray-600 text-sm mt-1">
                {lesson.description}
              </p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default Lessons;
