// src/pages/Exercise.jsx
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

function Exercise() {
  const [lessons, setLessons] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const { user, token } = useAuth();

  useEffect(() => {
    // ถ้าไม่ได้ login ให้ไปหน้า login
    if (!user || !token) {
      navigate("/login");
      return;
    }

    // Fetch lessons จาก backend
    fetch("http://localhost:3000/lessons", {
      headers: {
        Authorization: `Bearer ${token}`, // ถ้า backend ใช้ Bearer token
      },
    })
      .then((res) => res.json())
      .then((data) => setLessons(data))
      .catch((err) => console.error("Error fetching lessons:", err))
      .finally(() => setLoading(false));
  }, [user, token, navigate]);

  const handleLessonClick = (lessonId) => {
    navigate(`/exercises/lesson/${lessonId}`);
  };

  if (loading) {
    return <div className="text-center py-12">กำลังโหลดบทเรียน...</div>;
  }

  return (
    <div className="px-6 py-12">
      <div className="container mx-auto text-center">
        <h1 className="text-3xl font-bold">แบบฝึกหัด</h1>
        <p className="text-gray-600 mt-2">ทั้งหมด {lessons.length} บท</p>
      </div>

      <div className="container mx-auto grid grid-cols-1 md:grid-cols-2 gap-6 mt-8">
        {lessons.map((lesson, index) => (
          <div
            key={lesson.id || index}
            onClick={() => handleLessonClick(lesson.id)}
            className="flex items-center bg-gray-100 p-4 rounded-lg shadow-sm hover:shadow-md cursor-pointer transition"
          >
            <div className="flex-shrink-0 w-12 h-12 flex items-center justify-center bg-blue-600 text-white rounded-full text-lg font-bold">
              {index + 1}
            </div>
            <div className="ml-4 text-left">
              <h3 className="font-semibold text-lg">
                บทที่ {index + 1}: {lesson.title}
              </h3>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Exercise;
