import React, { useState, useEffect } from "react"; // âœ… à¹€à¸žà¸´à¹ˆà¸¡ useState, useEffect
import { useNavigate } from "react-router-dom";   // âœ… à¹€à¸žà¸´à¹ˆà¸¡ useNavigate
import { Users, Barcode } from "lucide-react";
import Card from "../components-Dashboard/Card";
import ContentSection from "../components-Dashboard/ContentSection";
import AdminTable from "../components-Dashboard/AdminTable";
import Header from "../components-Dashboard/Header";
import Sidebar from "../components-Dashboard/Sidebar";

export default function Dashboard() {
  const navigate = useNavigate(); // âœ… à¸ªà¸£à¹‰à¸²à¸‡ navigate
  const [lessons, setLessons] = useState([]);
  const [words, setWords] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [admins, setAdmins] = useState([]);

  useEffect(() => {
    fetchLessons();
    fetchWords();
    fetchQuestions();
    fetchAdmins();
  }, []);

  const fetchLessons = async () => {
    try {
      const res = await fetch("http://localhost:3000/lessons");
      const data = await res.json();
      setLessons(data); // âœ… à¹€à¸à¹‡à¸š object à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸”
    } catch (err) {
      console.error("Error fetching lessons:", err);
    }
  };

  const fetchWords = async () => {
    try {
      const res = await fetch("http://localhost:3000/signs");
      const data = await res.json();
      setWords(data); // âœ… à¹€à¸à¹‡à¸š object à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸”
    } catch (err) {
      console.error("Error fetching signs:", err);
    }
  };

  const fetchQuestions = async () => {
    try {
      const res = await fetch("http://localhost:3000/exercises");
      const data = await res.json();
      setQuestions(data); // âœ… à¹€à¸à¹‡à¸š object à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸”
    } catch (err) {
      console.error("Error fetching exercises:", err);
    }
  };

  const fetchAdmins = async () => {
    try {
      const res = await fetch("http://localhost:3000/admins");
      const data = await res.json();
      setAdmins(data);
    } catch (err) {
      console.error("Error fetching admins:", err);
    }
  };

  return (
    <div className="flex min-h-screen bg-gray-50">
      <Sidebar />

      <div className="flex-1 p-8">
        <Header />
        <h1 className="text-3xl font-bold text-gray-800 mb-8">ðŸ“Š Dashboard</h1>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-10">
          <Card title="Total Users" value="120" icon={Users} />
          <Card title="Content Items" value="53" icon={Barcode} />
        </div>

        <section>
          <h2 className="text-2xl font-semibold mb-4 text-gray-700">
            Content Management
          </h2>
          <div className="flex justify-between items-center mb-6">
            <input
              type="text"
              placeholder="Search..."
              className="border border-gray-300 rounded-md px-3 py-2 text-sm w-1/2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <button className="bg-blue-600 text-white px-5 py-2 rounded-md hover:bg-blue-700 transition">
              + Add
            </button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <ContentSection
              title="à¸šà¸—à¹€à¸£à¸µà¸¢à¸™"
              items={lessons}
              onEdit={(lesson) => navigate(`/edit-lesson/${lesson.lesson_id}`)}
              onDelete={async (lesson) => {
                if (window.confirm("à¸„à¸¸à¸“à¹à¸™à¹ˆà¹ƒà¸ˆà¸§à¹ˆà¸²à¸•à¹‰à¸­à¸‡à¸à¸²à¸£à¸¥à¸š?")) {
                  await fetch(`http://localhost:3000/lessons/${lesson.lesson_id}`, { method: "DELETE" });
                  fetchLessons();
                }
              }}
            />
            <ContentSection
              title="à¸„à¸³à¸¨à¸±à¸žà¸—à¹Œ"
              items={words}
              onEdit={(word) => navigate(`/edit-word/${word.word_id}`)}
              onDelete={async (word) => {
                if (window.confirm("à¸„à¸¸à¸“à¹à¸™à¹ˆà¹ƒà¸ˆà¸§à¹ˆà¸²à¸•à¹‰à¸­à¸‡à¸à¸²à¸£à¸¥à¸š?")) {
                  await fetch(`http://localhost:3000/signs/${word.word_id}`, { method: "DELETE" });
                  fetchWords();
                }
              }}
            />
            <ContentSection
              title="à¸„à¸³à¸–à¸²à¸¡"
              items={questions}
              onEdit={(q) => navigate(`/edit-question/${q.question_id}`)}
              onDelete={async (q) => {
                if (window.confirm("à¸„à¸¸à¸“à¹à¸™à¹ˆà¹ƒà¸ˆà¸§à¹ˆà¸²à¸•à¹‰à¸­à¸‡à¸à¸²à¸£à¸¥à¸š?")) {
                  await fetch(`http://localhost:3000/exercises/${q.question_id}`, { method: "DELETE" });
                  fetchQuestions();
                }
              }}
            />
          </div>
        </section>

        <section className="mt-10">
          <h2 className="text-2xl font-semibold mb-4 text-gray-700">
            Admin Management
          </h2>
          <AdminTable data={admins} />
        </section>
      </div>
    </div>
  );
}
