import React, { useState } from "react";
// เพิ่มบรรทัดนี้: import รูปภาพ heroImage
import heroImage from "src/assets\hero.webp"; // เปลี่ยน path ให้ตรงกับตำแหน่งไฟล์รูปจริงของคุณ

function AdminLogin() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();
    // ใส่ logic การ login ที่นี่ เช่น fetch API
    console.log("Username:", username);
    console.log("Password:", password);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="flex w-full max-w-4xl shadow-lg rounded-lg overflow-hidden">
        {/* ภาพประกอบ */}
        <div className="hidden md:block md:w-1/3">
          <img
            src={heroImage}
            alt="Admin Illustration"
            className="h-full w-full object-cover"
          />
        </div>

        {/* ฟอร์ม login */}
        <div className="w-full md:w-2/3 bg-white p-8 flex flex-col justify-center">
          <div className="text-center mb-6">
            <div className="flex justify-center mb-2">
              <svg
                className="w-16 h-16 text-gray-500"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M5.121 17.804A9 9 0 1112 21a9 9 0 01-6.879-3.196z"
                />
              </svg>
            </div>
            <h2 className="text-2xl font-bold">Welcome Admin</h2>
          </div>

          <form onSubmit={handleLogin} className="space-y-4">
            <div>
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
            </div>
            <div>
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
            </div>
            <button
              type="submit"
              className="w-full bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 transition-colors"
            >
              Log In
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default AdminLogin;
