import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

export default function EditExercise() {
  const { id } = useParams();
  const navigate = useNavigate();
  const MySwal = withReactContent(Swal);

  const [exercise, setExercise] = useState({
    question_text: "",
    correct_answer: "",
    choices: "",
    lesson_id: ""
  });

  useEffect(() => {
    fetch(`http://localhost:3000/exercises/${id}`)
      .then(res => res.json())
      .then(data => setExercise(data))
      .catch(err => console.error("Error:", err));
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setExercise(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const result = await MySwal.fire({
      title: "ยืนยันการแก้ไข?",
      showCancelButton: true,
      confirmButtonText: "ตกลง",
      cancelButtonText: "ยกเลิก"
    });
    if (!result.isConfirmed) return;

    try {
      const res = await fetch(`http://localhost:3000/exercises/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(exercise)
      });
      if (!res.ok) throw new Error("Failed to update exercise");
      MySwal.fire("สำเร็จ", "แก้ไขคำถามเรียบร้อยแล้ว", "success");
      navigate("/dashboard");
    } catch (err) {
      MySwal.fire("ผิดพลาด", err.message, "error");
    }
  };

  return (
    <div className="max-w-lg mx-auto p-8 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-6">แก้ไขคำถาม</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <textarea name="question_text" value={exercise.question_text} onChange={handleChange} placeholder="Question" className="w-full border px-3 py-2 rounded-md" />
        <input name="correct_answer" value={exercise.correct_answer} onChange={handleChange} placeholder="Correct Answer" className="w-full border px-3 py-2 rounded-md" />
        <input name="choices" value={exercise.choices} onChange={handleChange} placeholder='["A","B","C","D"]' className="w-full border px-3 py-2 rounded-md" />
        <input name="lesson_id" value={exercise.lesson_id} onChange={handleChange} placeholder="Lesson ID" className="w-full border px-3 py-2 rounded-md" />
        <button type="submit" className="bg-blue-600 text-white px-5 py-2 rounded-md">บันทึก</button>
      </form>
    </div>
  );
}
