import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function Testexercise() {
    const { id } = useParams();
    const [lesson, setLesson] = useState(null);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [selectedOption, setSelectedOption] = useState("");
    const [score, setScore] = useState(0);
    const [showResult, setShowResult] = useState(false);

    useEffect(() => {
        fetch(`http://localhost:3000/exercises/${id}`)
            .then(res => res.json())
            .then(data => setLesson(data))
            .catch(err => console.error("Error:", err));
    }, [id]);

    if (!lesson) return <p>Loading...</p>;

    const questions = lesson.questions;

    const handleNext = () => {
        if (selectedOption === questions[currentQuestion].answer) {
            setScore(score + 1);
        }
        setSelectedOption("");
        if (currentQuestion + 1 < questions.length) {
            setCurrentQuestion(currentQuestion + 1);
        } else {
            setShowResult(true);
        }
    };

    if (showResult) {
        return (
            <div className="px-6 py-12">
                <h1 className="text-3xl font-bold">ผลลัพธ์ของคุณ</h1>
                <p className="mt-4 text-lg">คุณทำถูก {score} / {questions.length} ข้อ</p>
            </div>
        );
    }

    const question = questions[currentQuestion];

    return (
        <div className="px-6 py-12">
            <h1 className="text-3xl font-bold">{lesson.title}</h1>
            <p className="mt-2 text-gray-600">คำถาม {currentQuestion + 1} / {questions.length}</p>

            <div className="mt-6 bg-gray-100 p-4 rounded-lg shadow">
                <h3 className="text-lg font-semibold">{question.question}</h3>
                <div className="mt-4 flex flex-col gap-3">
                    {question.options.map((opt, idx) => (
                        <button
                            key={idx}
                            className={`p-3 rounded border text-left ${selectedOption === opt ? "bg-blue-500 text-white" : "bg-white"}`}
                            onClick={() => setSelectedOption(opt)}
                        >
                            {opt}
                        </button>
                    ))}
                </div>
                <button
                    className="mt-4 px-4 py-2 bg-green-500 text-white rounded"
                    onClick={handleNext}
                    disabled={!selectedOption}
                >
                    ถัดไป
                </button>
            </div>
        </div>
    );
}

export default Testexercise;
