import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

export default function AddAdmin() {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    email: "",
  });

  const navigate = useNavigate();
  const MySwal = withReactContent(Swal);

  // ฟังก์ชันเปลี่ยนค่าใน input
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // ฟังก์ชันบันทึกข้อมูล
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.username || !formData.password) {
      MySwal.fire("กรอกข้อมูลไม่ครบ!", "กรุณากรอกชื่อผู้ใช้และรหัสผ่าน", "warning");
      return;
    }

    try {
      const res = await fetch("http://localhost:3000/admins/create", {
  method: "POST",
  headers: { "Content-Type": "application/json" },
  body: JSON.stringify(formData),
});


      if (res.ok) {
        MySwal.fire("สำเร็จ!", "เพิ่มผู้ดูแลระบบเรียบร้อยแล้ว", "success").then(() => {
          navigate("/dashboard"); // ✅ กลับไปหน้า dashboard
        });
      } else {
        MySwal.fire("ผิดพลาด!", "ไม่สามารถเพิ่มผู้ดูแลระบบได้", "error");
      }
    } catch (err) {
      console.error(err);
      MySwal.fire("เกิดข้อผิดพลาด!", "ไม่สามารถเชื่อมต่อเซิร์ฟเวอร์ได้", "error");
    }
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <form
        onSubmit={handleSubmit}
        className="bg-white p-8 rounded-xl shadow-md w-full max-w-md"
      >
        <h2 className="text-2xl font-bold mb-6 text-center">เพิ่มผู้ดูแลระบบ</h2>

        <div className="mb-4">
          <label className="block mb-1 text-gray-600">ชื่อผู้ใช้ (Username)</label>
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleChange}
            className="w-full border rounded-lg px-3 py-2"
            placeholder="กรอกชื่อผู้ใช้"
          />
        </div>

        <div className="mb-4">
          <label className="block mb-1 text-gray-600">อีเมล (Email)</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="w-full border rounded-lg px-3 py-2"
            placeholder="กรอกอีเมล"
          />
        </div>

        <div className="mb-6">
          <label className="block mb-1 text-gray-600">รหัสผ่าน (Password)</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className="w-full border rounded-lg px-3 py-2"
            placeholder="กรอกรหัสผ่าน"
          />
        </div>

        <div className="flex justify-between">
          <button
            type="button"
            onClick={() => navigate("/dashboard")}
            className="bg-gray-400 hover:bg-gray-500 text-white px-4 py-2 rounded-lg"
          >
            ย้อนกลับ
          </button>
          <button
            type="submit"
            className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg"
          >
            บันทึก
          </button>
        </div>
      </form>
    </div>
  );
}
