import { useEffect, useState, useCallback } from "react";
import { useParams } from "react-router-dom";

function Testexercise() {
  const { lessonNumber } = useParams();
  const [lesson, setLesson] = useState(null);
  const [score, setScore] = useState(0);
  const [leaderboard, setLeaderboard] = useState([]);

  const userId = localStorage.getItem("userId") || 0;
  const token = localStorage.getItem("token") || "";

  // Fetch lesson (เฉพาะ questions)
  useEffect(() => {
    fetch(`http://localhost:3000/exercises/lesson/${lessonNumber}`)
      .then((res) => res.json())
      .then((data) => setLesson(data))
      .catch((err) => console.error("Error fetching lesson:", err));
  }, [lessonNumber]);

  // ฟังก์ชันทดสอบส่ง score
  const handleFinish = useCallback(async () => {
    try {
      console.log("Sending score...");
      const res = await fetch("http://localhost:3000/scores", {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
        body: JSON.stringify({
          user_id: userId,          // ถ้า backend ใช้ token, อาจไม่ต้องส่ง
          lesson_number: lessonNumber,
          exercise_id: lessonNumber, // ชั่วคราวใส่ lessonNumber
          score: score,
        }),
      });

      const data = await res.json();
      console.log("POST /scores response:", data);

      // โหลด leaderboard
      const lbRes = await fetch(`http://localhost:3000/scores?lesson_number=${lessonNumber}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const lbData = await lbRes.json();
      console.log("Leaderboard:", lbData);
      setLeaderboard(lbData);
    } catch (err) {
      console.error("Save score error:", err);
    }
  }, [lessonNumber, score, userId, token]);

  // ทดลองเรียกทันทีหลังโหลด lesson
  useEffect(() => {
    if (lesson) {
      setScore(5); // ตัวอย่าง score
      handleFinish();
    }
  }, [lesson, handleFinish]);

  return (
    <div>
      <h1>Testexercise Minimal</h1>
      <p>Lesson: {lessonNumber}</p>
      <p>Score sent: {score}</p>
      <pre>{JSON.stringify(leaderboard, null, 2)}</pre>
    </div>
  );
}

export default Testexercise;
