import { useNavigate } from "react-router-dom";
import { FiX } from "react-icons/fi"; // ‚úÖ ‡πÑ‡∏≠‡∏Ñ‡∏≠‡∏ô‡∏Å‡∏≤‡∏Å‡∏ö‡∏≤‡∏ó
import Swal from "sweetalert2";
