// src/components/Navbar.js
import { useState, useEffect, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { ChevronDown } from "lucide-react";
import { useAuth } from "../context/AuthContext";

function Navbar() {
  const { user, setUser, logout, token } = useAuth();
  const navigate = useNavigate();

  const [lessons, setLessons] = useState([]);
  const [openMenu, setOpenMenu] = useState(null);
  const [userMenuOpen, setUserMenuOpen] = useState(false);
  const timeoutRef = useRef(null);

  // โหลดบทเรียน
  useEffect(() => {
    fetch("http://localhost:3000/lessons")
      .then((res) => res.json())
      .then((data) => setLessons(data))
      .catch((err) => console.error("Error fetching lessons:", err));
  }, []);

  // โหลดข้อมูล user จาก backend ตาม id
  useEffect(() => {
    if (!user?.user_id) return; // ต้องมี user_id จาก context

    fetch(`http://localhost:3000/user/${user.user_id}`, {
      headers: { Authorization: token },
    })
      .then((res) => res.json())
      .then((data) => {
        setUser(data); // อัปเดต context user
      })
      .catch((err) => console.error("Error fetching user:", err));
  }, [user?.user_id, token, setUser]);

  const handleMouseEnter = (menu) => {
    clearTimeout(timeoutRef.current);
    setOpenMenu(menu);
  };

  const handleMouseLeave = () => {
    timeoutRef.current = setTimeout(() => {
      setOpenMenu(null);
    }, 150);
  };

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <nav className="bg-white shadow-md p-4 sticky top-0 z-50">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-xl font-bold text-blue-600">THAI SIGN</h1>

        <ul className="flex gap-8 relative items-center">
          <li>
            <Link to="/" className="hover:text-blue-500 transition-colors">
              หน้าแรก
            </Link>
          </li>

          {/* เมนูบทเรียน */}
          <li
            className="relative"
            onMouseEnter={() => handleMouseEnter("lessons")}
            onMouseLeave={handleMouseLeave}
          >
            <div className="flex items-center gap-1 cursor-pointer hover:text-blue-500 select-none">
              <Link to="/lessons" className="hover:text-blue-500">
                บทเรียน
              </Link>
              <ChevronDown
                size={16}
                className={`transition-transform duration-300 ${
                  openMenu === "lessons" ? "rotate-180 text-blue-500" : ""
                }`}
              />
            </div>

            <div
              className={`absolute left-0 mt-2 w-56 bg-white shadow-lg rounded-lg border border-gray-200 transform transition-all duration-300 ease-in-out origin-top z-50 ${
                openMenu === "lessons"
                  ? "scale-y-100 opacity-100"
                  : "scale-y-0 opacity-0 pointer-events-none"
              } max-h-64 overflow-y-auto`}
            >
              <ul>
                {lessons.map((lesson, index) => (
                  <li key={lesson.id || index}>
                    <Link
                      to={`/lessons/${lesson.id}`}
                      className="block px-4 py-2 hover:bg-blue-50 transition-colors"
                    >
                      บท {lesson.number} : {lesson.title}
                    </Link>
                  </li>
                ))}
              </ul>
            </div>
          </li>

          {/* เมนูแบบฝึกหัด */}
          <li
            className="relative"
            onMouseEnter={() => handleMouseEnter("exercises")}
            onMouseLeave={handleMouseLeave}
          >
            <div className="flex items-center gap-1 cursor-pointer hover:text-blue-500 select-none">
              <Link to="/exercises" className="hover:text-blue-500">
                แบบฝึกหัด
              </Link>
              <ChevronDown
                size={16}
                className={`transition-transform duration-300 ${
                  openMenu === "exercises" ? "rotate-180 text-blue-500" : ""
                }`}
              />
            </div>

            <div
              className={`absolute left-0 mt-2 w-56 bg-white shadow-lg rounded-lg border border-gray-200 transform transition-all duration-300 ease-in-out origin-top z-50 ${
                openMenu === "exercises"
                  ? "scale-y-100 opacity-100"
                  : "scale-y-0 opacity-0 pointer-events-none"
              } max-h-64 overflow-y-auto`}
            >
              <ul>
                {lessons.map((lesson, index) => (
                  <li key={lesson.id || index}>
                    <Link
                      to={`/exercises/lesson/${lesson.id}`}
                      className="block px-4 py-2 hover:bg-blue-50 transition-colors"
                    >
                      แบบฝึกหัดบทที่ {lesson.number} : {lesson.title}
                    </Link>
                  </li>
                ))}
              </ul>
            </div>
          </li>

          {/* ส่วน Login / User */}
          {user ? (
            <li className="relative">
              <div
                className="flex items-center gap-2 cursor-pointer select-none"
                onClick={() => setUserMenuOpen(!userMenuOpen)}
              >
                <span className="font-semibold">{user.username}</span>
                <ChevronDown
                  size={16}
                  className={`transition-transform ${
                    userMenuOpen ? "rotate-180" : ""
                  }`}
                />
              </div>
              {userMenuOpen && (
                <div className="absolute right-0 mt-2 w-32 bg-white shadow-lg rounded-lg border border-gray-200 z-50">
                  <ul>
                    <li>
                      <button
                        onClick={handleLogout}
                        className="w-full text-left px-4 py-2 hover:bg-blue-50 transition-colors"
                      >
                        Logout
                      </button>
                    </li>
                  </ul>
                </div>
              )}
            </li>
          ) : (
            <>
              <li>
                <Link
                  to="/login"
                  className="px-4 py-2 text-blue-600 border border-blue-600 rounded hover:bg-blue-500 hover:text-white transition-colors"
                >
                  Login
                </Link>
              </li>
              <li>
                <Link
                  to="/guest-login"
                  className="px-4 py-2 text-blue-600 border border-blue-600 rounded hover:bg-blue-500 hover:text-white transition-colors"
                >
                  Guest
                </Link>
              </li>
            </>
          )}
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
