// src/components/Navbar.jsx
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

function Navbar() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <nav className="bg-white shadow-md p-4 sticky top-0 z-50">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-xl font-bold text-blue-600">THAI SIGN</h1>

        <ul className="flex gap-4 items-center">
          <li>
            <Link to="/" className="hover:text-blue-500">
              หน้าแรก
            </Link>
          </li>
          <li>
            <Link to="/lessons" className="hover:text-blue-500">
              บทเรียน
            </Link>
          </li>
          <li>
            <Link to="/exercises" className="hover:text-blue-500">
              แบบฝึกหัด
            </Link>
          </li>

          {/* แสดง username ถ้า login แล้ว */}
          {user ? (
            <>
              <li className="font-semibold">{user.username}</li>
              <li>
                <button
                  onClick={handleLogout}
                  className="px-3 py-1 text-white bg-blue-600 rounded hover:bg-blue-500"
                >
                  Logout
                </button>
              </li>
            </>
          ) : (
            <>
              <li>
                <Link
                  to="/login"
                  className="px-3 py-1 text-blue-600 border border-blue-600 rounded hover:bg-blue-500 hover:text-white"
                >
                  Login
                </Link>
              </li>
            </>
          )}
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
