import { useEffect, useState, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";

function Testexercise() {
  const { lessonNumber } = useParams();
  const navigate = useNavigate();

  const [lesson, setLesson] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [userAnswers, setUserAnswers] = useState([]);
  const [showAnswers, setShowAnswers] = useState(false);
  const [timeLeft, setTimeLeft] = useState(90);
  const [leaderboard, setLeaderboard] = useState([]);

  // ‡∏î‡∏∂‡∏á userId ‡∏à‡∏≤‡∏Å localStorage
  const userId = Number(localStorage.getItem("userId") || 0); // ‚úÖ number
  const token = localStorage.getItem("token") || "";


  const finalScore = score;

  // Fetch lesson
  useEffect(() => {
    fetch(`http://localhost:3000/exercises/lesson/${lessonNumber}`)
      .then((res) => res.json())
      .then((data) => setLesson(data))
      .catch((err) => console.error("Error:", err));
  }, [lessonNumber]);

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const sec = seconds % 60;
    return `${minutes}:${sec < 10 ? "0" : ""}${sec}`;
  };

  const handleFinish = useCallback(async () => {
    const questions = lesson?.questions || [];

    if (currentQuestion < questions.length) {
      const answer = selectedOption || "‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å";
      setUserAnswers((prev) => [...prev, answer]);
      if (answer === questions[currentQuestion].answer) setScore((prev) => prev + 1);
    }

    // POST score
    try {
      if (userId && token) {
        const res = await fetch("http://localhost:3000/scores", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
          },
          body: JSON.stringify({
            user_id: userId, // ‚úÖ ‡∏™‡πà‡∏á‡πÄ‡∏õ‡πá‡∏ô number
            lesson_number: Number(lessonNumber),
            exercise_id: Number(lessonNumber),
            score: finalScore,
          }),

        });
        const data = await res.json();
        console.log("POST /scores response:", data);
      }
    } catch (err) {
      console.error("Save score error:", err);
    }

    // Load leaderboard
    try {
      const lbRes = await fetch(`http://localhost:3000/scores?lesson_number=${lessonNumber}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const lbData = await lbRes.json();
      lbData.sort((a, b) => b.score - a.score);
      setLeaderboard(lbData.slice(0, 5));
    } catch (err) {
      console.error("Load leaderboard error:", err);
    }

    setShowResult(true);
  }, [currentQuestion, selectedOption, score, lesson, userId, token, lessonNumber, finalScore]);

  // Countdown timer
  useEffect(() => {
    if (showResult) return;
    const timer = setInterval(() => {
      setTimeLeft((prev) => {
