import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function AddLesson() {
  const navigate = useNavigate();
  const [lessonNumber, setLessonNumber] = useState("");
  const [lessonTitle, setLessonTitle] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("lesson_number", lessonNumber);
    formData.append("lesson_title", lessonTitle);
    formData.append("description", description);
    if (image) formData.append("image", image); // ✅ เปลี่ยนตรงนี้

    try {
      const res = await fetch("http://localhost:3000/lessons", {
        method: "POST",
        body: formData,
      });
      if (res.ok) {
        alert("Lesson added successfully");
        navigate("/dashboard");
      } else {
        const errData = await res.json();
        alert("Add lesson failed: " + errData.error);
      }
    } catch (err) {
      console.error(err);
      alert("Failed to add lesson");
    }
  };

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-6">Add Lesson</h1>
      <form onSubmit={handleSubmit} className="flex flex-col gap-4 max-w-md">
        <input
          type="number"
          placeholder="Lesson Number"
          value={lessonNumber}
          onChange={(e) => setLessonNumber(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Lesson Title"
          value={lessonTitle}
          onChange={(e) => setLessonTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setImage(e.target.files[0])}
        />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
          Save
        </button>
      </form>
    </div>
  );
}
