import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

function Variouslessons() {
  const { id } = useParams();
  const [lesson, setLesson] = useState(null);
  const [signs, setSigns] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    setError(null);

    // ดึงข้อมูลบทเรียนและคำศัพท์พร้อมกัน
    const fetchLesson = fetch(`http://localhost:3000/api/lessons/${id}`).then((res) => {
      if (!res.ok) throw new Error("ไม่พบบทเรียน");
      return res.json();
    });

    const fetchSigns = fetch(`http://localhost:3000/api/signs/lesson/${id}`).then((res) => {
      if (!res.ok) throw new Error("ไม่พบคำศัพท์");
      return res.json();
    });

    Promise.all([fetchLesson, fetchSigns])
      .then(([lessonData, signsData]) => {
        setLesson(lessonData);
        setSigns(signsData);
      })
      .catch((err) => setError(err.message))
      .finally(() => setLoading(false));
  }, [id]);

  if (loading) return <p className="p-6">กำลังโหลดข้อมูล...</p>;
  if (error) return <p className="p-6 text-red-500">เกิดข้อผิดพลาด: {error}</p>;
  if (!lesson) return <p className="p-6">ไม่พบบทเรียน</p>;

  const prevId = lesson.id > 1 ? lesson.id - 1 : null;
  const nextId = lesson.id + 1;

  return (
    <div className="p-6">
      {/* ปุ่มย้อนกลับ */}
      <div className="flex justify-between mb-6">
        <Link to="/lessons" className="text-blue-600 hover:underline">
          ← กลับไปหน้าบทเรียน
        </Link>

        <div className="space-x-2">
          {prevId && (
            <Link
              to={`/lessons/${prevId}`}
              className="px-4 py-2 bg-gray-200 rounded hover:bg-gray-300 transition"
            >
              บทเรียนก่อนหน้า
            </Link>
          )}
          {nextLesson && (
            <Link
              to={`/lessons/${nextLesson.id}`}
              className="px-4 py-2 bg-gray-200 rounded hover:bg-gray-300 transition"
            >
              บทเรียนถัดไป
            </Link>
          )}
        </div>
      </div>

        {/* ชื่อบทเรียน */}
        <h2 className="text-2xl font-bold mt-4">
          บทเรียนที่ {lesson.lesson_number || lesson.id}: {lesson.title}
        </h2>
        <p className="mt-2 text-gray-700">{lesson.description}</p>

        {/* คำศัพท์/ท่ามือ */}
        <h3 className="text-xl font-semibold mt-6">คำศัพท์ / ท่ามือ</h3>
        {signs.length > 0 ? (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-4">
            {signs.map((sign) => (
              <div
                key={sign.sign_id}
                className="border rounded-lg p-4 shadow hover:shadow-lg transition"
              >
                <h4 className="font-semibold text-lg">{sign.word}</h4>
                <p className="text-gray-600">{sign.meaning}</p>
                {sign.image_url && (
                  <img
                    src={`http://localhost:3000/${sign.image_url}`}
                    alt={sign.word}
                    className="mt-2 w-full h-40 object-contain"
                  />
                )}
                {sign.video_url && (
                  <video
                    controls
                    className="mt-2 w-full rounded-lg"
                    src={`http://localhost:3000/${sign.video_url}`}
                  />
                )}
              </div>
            ))}
          </div>
        ) : (
          <p className="mt-2 text-gray-500">ยังไม่มีคำศัพท์ในบทนี้</p>
        )}
      </div>
      );
}

      export default Variouslessons;
