import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

export default function EditAdmin() {
  const { id } = useParams();
  const navigate = useNavigate();
  const MySwal = withReactContent(Swal);

  const [admin, setAdmin] = useState({
    admin_id: "",
    username: "",
    email: "",
    password: ""
  });

  useEffect(() => {
    fetch(`http://localhost:3000/admins/${id}`)
      .then(res => res.json())
      .then(data => {
        setAdmin({
          admin_id: data.admin_id,
          username: data.username,
          email: data.email,
          password: "" // เวลาจะแก้ไขให้กรอกรหัสใหม่เท่านั้น
        });
      })
      .catch(err => console.error("Error fetching admin:", err));
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAdmin(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const result = await MySwal.fire({
      title: "ยืนยันการบันทึก",
      showCancelButton: true,
      confirmButtonText: "ตกลง",
      cancelButtonText: "ยกเลิก"
    });
    if (!result.isConfirmed) return;

    try {
      if (!admin.name || !admin.email) throw new Error("กรุณากรอกชื่อและอีเมล");

      const body = {
        name: admin.name,
        email: admin.email
      };
      if (admin.password) body.password = admin.password; // อัปเดตรหัสเฉพาะเมื่อกรอก

      const res = await fetch(`http://localhost:3000/admins/${admin.admin_id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body)
      });

      if (!res.ok) {
        const text = await res.text();
        throw new Error(`Update failed: ${text}`);
      }

      MySwal.fire("สำเร็จ!", "แก้ไขผู้ดูแลระบบเรียบร้อยแล้ว", "success");
      navigate("/dashboard");
    } catch (err) {
      console.error("Error updating admin:", err);
      MySwal.fire("ผิดพลาด", `เกิดข้อผิดพลาดในการแก้ไขผู้ดูแลระบบ\n${err.message}`, "error");
    }
  };

  return (
    <div className="max-w-lg mx-auto p-8 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-6">แก้ไขผู้ดูแลระบบ</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="name"
          value={admin.name}
          onChange={handleChange}
          placeholder="ชื่อ"
          className="w-full border px-3 py-2 rounded-md"
        />
        <input
          type="email"
          name="email"
          value={admin.email}
          onChange={handleChange}
          placeholder="อีเมล"
          className="w-full border px-3 py-2 rounded-md"
        />
        <input
          type="password"
          name="password"
          value={admin.password}
          onChange={handleChange}
          placeholder="รหัสผ่าน (กรอกเฉพาะถ้าอยากเปลี่ยน)"
          className="w-full border px-3 py-2 rounded-md"
        />
        <button type="submit" className="bg-blue-600 text-white px-5 py-2 rounded-md">บันทึก</button>
      </form>
    </div>
  );
}
