import { useEffect, useState } from "react";

function Exercise() {
  const [exercises, setExercises] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/exercises")
      .then((res) => res.json())
      .then((data) => setExercises(data))
      .catch((err) => console.error("Error:", err));
  }, []);

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-8">
        {exercises.map((exercise, index) => (
          <div
            key={index}
            className="flex items-start bg-gray-100 p-6 rounded-lg shadow-sm hover:shadow-md transition"
          >
            {/* Circle Number */}
            <div className="flex-shrink-0 w-12 h-12 flex items-center justify-center bg-blue-600 text-white rounded-full text-lg font-bold">
              {exercise.id || index + 1}
            </div>

            {/* Lesson Content */}
            <div className="ml-4">
              <h3 className="font-semibold text-lg">
                บทที่ {exercise.id || index + 1}: {exercise.title}
              </h3>
              <p className="text-gray-600 text-sm mt-1">{lesson.description}</p>
            </div>
          </div>
        ))}
      </div>
  );
}

export default Exercise;
