import React, { useState } from "react";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";
import { useNavigate } from "react-router-dom";

export default function AddAdmin() {
  const navigate = useNavigate();
  const MySwal = withReactContent(Swal);

  const [admin, setAdmin] = useState({
    username: "",
    email: "",
    password: "",
    role: "admin",
  });

  const handleChange = (e) => {
    setAdmin({ ...admin, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:3000/admins", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(admin),
      });

      if (res.ok) {
        MySwal.fire("สำเร็จ!", "เพิ่มผู้ดูแลระบบเรียบร้อยแล้ว", "success");
        navigate("/dashboard");
      } else {
        MySwal.fire("ผิดพลาด!", "ไม่สามารถเพิ่มข้อมูลได้", "error");
      }
    } catch (err) {
      console.error(err);
      MySwal.fire("ผิดพลาด!", "เกิดข้อผิดพลาดในการเชื่อมต่อ", "error");
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 bg-white shadow-lg rounded-lg p-6">
      <h1 className="text-2xl font-semibold text-gray-800 mb-6">เพิ่มผู้ดูแลระบบ</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-gray-700 mb-1">ชื่อผู้ใช้</label>
          <input
            type="text"
            name="username"
            value={admin.username}
            onChange={handleChange}
            required
            className="w-full border rounded-md px-3 py-2 focus:ring-2 focus:ring-blue-400"
          />
        </div>

        <div>
          <label className="block text-gray-700 mb-1">อีเมล</label>
          <input
            type="email"
            name="email"
            value={admin.email}
            onChange={handleChange}
            required
            className="w-full border rounded-md px-3 py-2 focus:ring-2 focus:ring-blue-400"
          />
        </div>

        <div>
          <label className="block text-gray-700 mb-1">รหัสผ่าน</label>
          <input
            type="password"
            name="password"
            value={admin.password}
            onChange={handleChange}
            required
            className="w-full border rounded-md px-3 py-2 focus:ring-2 focus:ring-blue-400"
          />
        </div>

        <div>
          <label className="block text-gray-700 mb-1">บทบาท</label>
          <select
            name="role"
            value={admin.role}
            onChange={handleChange}
            className="w-full border rounded-md px-3 py-2 focus:ring-2 focus:ring-blue-400"
          >
            <option value="admin">Admin</option>
            <option value="superadmin">Super Admin</option>
          </select>
        </div>

        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 transition"
        >
          บันทึก
        </button>
      </form>
    </div>
  );
}
