// src/components/Navbar.jsx
import { useEffect, useState, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { ChevronDown, MoreVertical } from "lucide-react";
import { useAuth } from "../context/AuthContext";

function Navbar() {
  const { username, logout, user, token } = useAuth(); // เอา user, token มาใช้
  const [lessons, setLessons] = useState([]);
  const [openMenu, setOpenMenu] = useState(null);
  const [openUserMenu, setOpenUserMenu] = useState(false);
  const timeoutRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:3000/lessons")
      .then(res => res.json())
      .then(data => setLessons(data))
      .catch(err => console.error(err));
  }, []);

  const handleMouseEnter = (menu) => {
    clearTimeout(timeoutRef.current);
    setOpenMenu(menu);
  };

  const handleMouseLeave = () => {
    timeoutRef.current = setTimeout(() => setOpenMenu(null), 150);
  };

  // ฟังก์ชันตรวจสอบ login ก่อนเข้า
  const handleNavigate = (path) => {
    if (!user || !token) {
      navigate("/login");
    } else {
      navigate(path);
    }
  };

  return (
    <nav className="bg-white shadow-md p-4 sticky top-0 z-50">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-xl font-bold text-blue-600">THAI SIGN</h1>

        <ul className="flex gap-4 items-center relative">
          {/* หน้าแรก */}
          <li>
            <Link to="/" className="hover:text-blue-500 transition-colors">หน้าแรก</Link>
          </li>

          {/* Lessons Dropdown */}
          <li
            onMouseEnter={() => handleMouseEnter("lessons")}
            onMouseLeave={handleMouseLeave}
            className="relative"
          >
            <div className="flex items-center gap-1 cursor-pointer hover:text-blue-500">
              <span onClick={() => handleNavigate("/lessons")}>บทเรียน</span>
              <ChevronDown className={openMenu === "lessons" ? "rotate-180" : ""} size={16} />
            </div>

            <div
              className={`absolute left-0 mt-2 w-56 bg-white shadow-lg rounded-lg border border-gray-200 transform transition-all duration-300 ${
                openMenu === "lessons" ? "scale-y-100 opacity-100" : "scale-y-0 opacity-0 pointer-events-none"
              } max-h-64 overflow-y-auto`}
            >
              <ul>
                {lessons.map(l => (
                  <li key={l.id}>
                    <span
                      onClick={() => handleNavigate(`/lessons/${l.number}`)}
                      className="block px-4 py-2 hover:bg-blue-50 cursor-pointer"
                    >
                      บท {l.number} : {l.title}
                    </span>
                  </li>
                ))}
              </ul>
            </div>
          </li>

          {/* Exercises Dropdown */}
          <li
            onMouseEnter={() => handleMouseEnter("exercises")}
            onMouseLeave={handleMouseLeave}
            className="relative"
          >
            <div className="flex items-center gap-1 cursor-pointer hover:text-blue-500">
              <span onClick={() => handleNavigate("/exercises")}>แบบฝึกหัด</span>
              <ChevronDown className={openMenu === "exercises" ? "rotate-180" : ""} size={16} />
            </div>

            <div
              className={`absolute left-0 mt-2 w-56 bg-white shadow-lg rounded-lg border border-gray-200 transform transition-all duration-300 ${
                openMenu === "exercises" ? "scale-y-100 opacity-100" : "scale-y-0 opacity-0 pointer-events-none"
              } max-h-64 overflow-y-auto`}
            >
              <ul>
                {lessons.map(l => (
                  <li key={l.id}>
                    <span
                      onClick={() => handleNavigate(`/exercises/lesson/${l.id}`)}
                      className="block px-4 py-2 hover:bg-blue-50 cursor-pointer"
                    >
                      แบบฝึกหัดบทที่ {l.number} : {l.title}
                    </span>
                  </li>
                ))}
              </ul>
            </div>
          </li>

          {/* Username */}
          {username && (
            <li className="font-semibold text-blue-600">{username}</li>
          )}

          {/* MoreVertical Dropdown */}
          {username && (
            <li className="relative">
              <button onClick={() => setOpenUserMenu(!openUserMenu)} className="p-2 rounded hover:bg-gray-100">
                <MoreVertical className="w-5 h-5 text-gray-600" />
              </button>
              {openUserMenu && (
                <div className="absolute right-0 mt-2 w-40 bg-white shadow-lg rounded-md border border-gray-200">
                  <Link to="/score" className="block px-4 py-2 text-gray-800 hover:bg-gray-50">ดูคะแนน</Link>
                  <button onClick={logout} className="w-full text-left px-4 py-2 text-red-600 hover:bg-red-50">Logout</button>
                </div>
              )}
            </li>
          )}

          {/* Login */}
          {!username && (
            <li>
              <Link to="/login" className="border-2 border-green-500 text-green-600 px-4 py-2 rounded-lg hover:bg-green-500 hover:text-white">
                Login
              </Link>
            </li>
          )}

          {/* Admin */}
          {!username && (
            <li>
              <Link
                to="/admin/admin"
                className="border-2 border-blue-500 text-blue-600 px-4 py-2 rounded-lg hover:bg-blue-500 hover:text-white transition-colors"
              >
                Admin
              </Link>
            </li>
          )}
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
