import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

export default function AddExercise() {
  const navigate = useNavigate();
  const MySwal = withReactContent(Swal);

  const [exercise, setExercise] = useState({
    question: "",
    option_a: "",
    option_b: "",
    option_c: "",
    option_d: "",
    correct_answer: "",
  });

  const handleChange = (e) => {
    setExercise({ ...exercise, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:3000/exercises", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(exercise),
      });

      if (res.ok) {
        MySwal.fire("สำเร็จ!", "เพิ่มคำถามเรียบร้อยแล้ว", "success");
        navigate("/dashboard");
      } else {
        MySwal.fire("ผิดพลาด!", "ไม่สามารถเพิ่มคำถามได้", "error");
      }
    } catch (err) {
      console.error(err);
      MySwal.fire("ผิดพลาด!", "เกิดข้อผิดพลาดในการเชื่อมต่อ", "error");
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 bg-white shadow-lg rounded-lg p-6">
      <h1 className="text-2xl font-semibold mb-6 text-gray-800">เพิ่มคำถาม</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block mb-1 text-gray-700">คำถาม</label>
          <input
            type="text"
            name="question"
            value={exercise.question}
            onChange={handleChange}
            required
            className="w-full border rounded-md px-3 py-2 focus:ring-2 focus:ring-blue-400"
          />
        </div>
        {["a", "b", "c", "d"].map((opt) => (
          <div key={opt}>
            <label className="block mb-1 text-gray-700">{`ตัวเลือก ${opt.toUpperCase()}`}</label>
            <input
              type="text"
              name={`option_${opt}`}
              value={exercise[`option_${opt}`]}
              onChange={handleChange}
              className="w-full border rounded-md px-3 py-2 focus:ring-2 focus:ring-blue-400"
            />
          </div>
        ))}
        <div>
          <label className="block mb-1 text-gray-700">คำตอบที่ถูกต้อง (a,b,c,d)</label>
          <input
            type="text"
            name="correct_answer"
            value={exercise.correct_answer}
            onChange={handleChange}
            required
            className="w-full border rounded-md px-3 py-2 focus:ring-2 focus:ring-blue-400"
          />
        </div>
        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 transition"
        >
          บันทึก
        </button>
      </form>
    </div>
  );
}
