        <input type="text" placeholder="‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å A" value={optionA} onChange={(e) => setOptionA(e.target.value)} />
        <input type="text" placeholder="‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å B" value={optionB} onChange={(e) => setOptionB(e.target.value)} />
        <input type="text" placeholder="‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å C" value={optionC} onChange={(e) => setOptionC(e.target.value)} />
        <input type="text" placeholder="‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å D" value={optionD} onChange={(e) => setOptionD(e.target.value)} />
        <input type="text" placeholder="‡∏Ñ‡∏≥‡∏ï‡∏≠‡∏ö‡∏ó‡∏µ‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á" value={correctAnswer} onChange={(e) => setCorrectAnswer(e.target.value)} />

        <input type="file" accept="image/*" onChange={(e) => setImage(e.target.files[0])} />

        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
          ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å
        </button>
      </form>
    </div>
  );
}
