import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function Navbar() {
  const [lessons, setLessons] = useState([]);
  const [exercises, setExercises] = useState([]);
  const [openMenu, setOpenMenu] = useState(null); // เก็บสถานะว่าเมนูไหนเปิดอยู่

  useEffect(() => {
    // ดึงข้อมูลบทเรียนจาก API
    fetch("http://localhost:3000/lessons")
      .then((res) => res.json())
      .then((data) => setLessons(data))
      .catch((err) => console.error("Error fetching lessons:", err));

    // ดึงข้อมูลแบบฝึกหัดจาก API
    fetch("http://localhost:3000//exercises")
      .then((res) => res.json())
      .then((data) => setExercises(data))
      .catch((err) => console.error("Error fetching exercises:", err));
  }, []);

  return (
    <nav className="bg-white shadow-md p-4 sticky top-0 z-50">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-xl font-bold text-blue-600">THAI SIGN</h1>

        <ul className="flex gap-8 relative">
          <li>
            <Link to="/" className="hover:text-blue-500 transition-colors">
              หน้าแรก
            </Link>
          </li>

          {/* เมนูบทเรียน */}
          <li
            className="relative"
            onMouseEnter={() => setOpenMenu("lessons")}
            onMouseLeave={() => setOpenMenu(null)}
          >
            <span className="hover:text-blue-500 cursor-pointer">
              บทเรียน
            </span>

            {/* เมนูย่อย */}
            {openMenu === "lessons" && (
              <ul className="absolute left-0 mt-2 bg-white shadow-lg rounded-lg w-48 border border-gray-200">
                {lessons.map((lesson) => (
                  <li key={lesson.lesson_number}>
                    <Link
                      to={`/lessons/${lesson.lesson_number}`}
                      className="block px-4 py-2 hover:bg-blue-50"
                    >
                      {lesson.lesson_name}
                    </Link>
                  </li>
                ))}
              </ul>
            )}
          </li>

          {/* เมนูแบบฝึกหัด */}
          <li
            className="relative"
            onMouseEnter={() => setOpenMenu("exercises")}
            onMouseLeave={() => setOpenMenu(null)}
          >
            <span className="hover:text-blue-500 cursor-pointer">
              แบบฝึกหัด
            </span>

            {/* เมนูย่อย */}
            {openMenu === "exercises" && (
              <ul className="absolute left-0 mt-2 bg-white shadow-lg rounded-lg w-48 border border-gray-200">
                {exercises.map((ex) => (
                  <li key={ex.exercise_id}>
                    <Link
                      to={`/exercises/${ex.exercise_id}`}
                      className="block px-4 py-2 hover:bg-blue-50"
                    >
                      {ex.exercise_name}
                    </Link>
                  </li>
                ))}
              </ul>
            )}
          </li>

          {/* ปุ่ม Admin (มีกรอบและสีพิเศษ) */}
          <li>
            <Link
              to="/admin"
              className="border-2 border-blue-500 text-blue-600 px-4 py-2 rounded-lg hover:bg-blue-500 hover:text-white transition-colors"
            >
              Admin
            </Link>
          </li>
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
